     */
    function isInSegment(
        uint256 timestamp,
        uint256 startTimestamp,
        uint256 endTimestamp
    )
        internal
        pure
        returns (bool)
    {
        if (startTimestamp > endTimestamp) return false;
        if (startTimestamp <= timestamp && timestamp <= endTimestamp) return true;
        return false;
    }
}

