pragma solidity ^0.6.4;

import "../../external/BokkyPooBah/BokkyPooBahsDateTimeLibrary.sol";

import "../ACTUSTypes.sol";


/**
 * @title BusinessDayConvention
 * @notice Contains conventions of how to handle non-business days when generating schedules of events.
 * The events schedule time can be shifted or not, if shifted it is possible that it is shifted to the next
 * or previous valid business days, etc.
 */
contract BusinessDayConvention is ACTUSTypes {

    /**
     * @notice Used in POFs and STFs for DCFs.
     * No shifting is applied if a Calc/Shift instead of Shift/Calc BDC is provided.
     */
    function shiftCalcTime(
        uint256 timestamp,
        BusinessDayConvention convention,
        Calendar calendar
    )
        internal
        pure
        returns (uint256)
    {
        if (
            convention == BusinessDayConvention.CSF ||
            convention == BusinessDayConvention.CSMF ||
            convention == BusinessDayConvention.CSP ||
            convention == BusinessDayConvention.CSMP
        ) {
            return timestamp;
        }

        return shiftEventTime(timestamp, convention, calendar);
    }

    /*
     * @notice Used for generating event schedules (for single events and event cycles schedules).
     * This convention assumes that when shifting the events schedule time according
     * to a BDC, the time is shifted first and calculations are performed thereafter.
     * (Calculations in POFs and STFs are based on the shifted time as well)
     */
    function shiftEventTime(
        uint256 timestamp,
        BusinessDayConvention convention,
        Calendar calendar
    )
        internal
        pure
        returns (uint256)
    {
        // Shift/Calc Following, Calc/Shift following
        if (convention == BusinessDayConvention.SCF || convention == BusinessDayConvention.CSF) {
            return getClosestBusinessDaySameDayOrFollowing(timestamp, calendar);
        // Shift/Calc Modified Following, Calc/Shift Modified following
        // Same as unmodified if shifted date is in the same month, if not it returns the previous buiness-day
        } else if (convention == BusinessDayConvention.SCMF || convention == BusinessDayConvention.CSMF) {
            uint256 followingOrSameBusinessDay = getClosestBusinessDaySameDayOrFollowing(timestamp, calendar);
            if (BokkyPooBahsDateTimeLibrary.getMonth(followingOrSameBusinessDay) == BokkyPooBahsDateTimeLibrary.getMonth(timestamp)) {
                return followingOrSameBusinessDay;
            }
            return getClosestBusinessDaySameDayOrPreceeding(timestamp, calendar);
        // Shift/Calc Preceeding, Calc/Shift Preceeding
        } else if (convention == BusinessDayConvention.SCP || convention == BusinessDayConvention.CSP) {
            return getClosestBusinessDaySameDayOrPreceeding(timestamp, calendar);
        // Shift/Calc Modified Preceeding, Calc/Shift Modified Preceeding
        // Same as unmodified if shifted date is in the same month, if not it returns the following buiness-day
        } else if (convention == BusinessDayConvention.SCMP || convention == BusinessDayConvention.CSMP) {
            uint256 preceedingOrSameBusinessDay = getClosestBusinessDaySameDayOrPreceeding(timestamp, calendar);
            if (BokkyPooBahsDateTimeLibrary.getMonth(preceedingOrSameBusinessDay) == BokkyPooBahsDateTimeLibrary.getMonth(timestamp)) {
                return preceedingOrSameBusinessDay;
            }
            return getClosestBusinessDaySameDayOrFollowing(timestamp, calendar);
        }

        return timestamp;
    }

    /**
     * @notice Returns the following business day if a non-business day is provided.
     * (Returns the same day if calendar != MondayToFriday)
     */
    function getClosestBusinessDaySameDayOrFollowing(uint256 timestamp, Calendar calendar)
        internal
        pure
        returns (uint256)
    {
        if (calendar == Calendar.MF) {
            if (BokkyPooBahsDateTimeLibrary.getDayOfWeek(timestamp) == 6) {
                return BokkyPooBahsDateTimeLibrary.addDays(timestamp, 2);
            } else if (BokkyPooBahsDateTimeLibrary.getDayOfWeek(timestamp) == 7) {
                return BokkyPooBahsDateTimeLibrary.addDays(timestamp, 1);
            }
        }
        return timestamp;
    }

    /**
     * @notice Returns the previous buiness day if a non-businessday is provided.
     * (Returns the same day if calendar != MondayToFriday)
     */
    function getClosestBusinessDaySameDayOrPreceeding(uint256 timestamp, Calendar calendar)
        internal
        pure
        returns (uint256)
    {
        if (calendar == Calendar.MF) {
            if (BokkyPooBahsDateTimeLibrary.getDayOfWeek(timestamp) == 6) {
                return BokkyPooBahsDateTimeLibrary.subDays(timestamp, 1);
            } else if (BokkyPooBahsDateTimeLibrary.getDayOfWeek(timestamp) == 7) {
                return BokkyPooBahsDateTimeLibrary.subDays(timestamp, 2);
            }
        }
        return timestamp;
    }
}

contracts/Core/SharedTypes.sol.content:
pragma solidity ^0.6.4;

import "@atpar/actus-solidity/contracts/Core/ACTUSTypes.sol";


contract SharedTypes is ACTUSTypes {

    // offset == 0 is interpreted as a not set date value and not shifted
    // hence we define 1 as an offset == anchorDate
    uint256 constant ZERO_OFFSET = 1;

    struct TemplateSchedule {
        // scheduleTime and EventType are tighlty packed and encoded as bytes32
        // in the context of a Template scheduleTime is defined as an offset in seconds
        // respective to an anchorDate which is defined in the CustomTerms of the asset which references this template
        // index of event => bytes32 encoded event
        mapping(uint256 => bytes32) events;
        // the length of the schedule, used to determine the end of the schedule
        uint256 length;
    }

    struct AssetOwnership {
        // account which has to fulfill all obligations for the creator side
        address creatorObligor;
        // account to which all cashflows to which the creator is the beneficiary are forwarded
        address creatorBeneficiary;
        // account which has to fulfill all obligations for the counterparty
        address counterpartyObligor;
        // account to which all cashflows to which the counterparty is the beneficiary are forwarded
        address counterpartyBeneficiary;
    }

    // modified set of LifecycleTerms which does not contain ContractReferences
    struct TemplateTerms {
        Calendar calendar;
        ContractRole contractRole;
        DayCountConvention dayCountConvention;
        BusinessDayConvention businessDayConvention;
        EndOfMonthConvention endOfMonthConvention;
        ScalingEffect scalingEffect;
        PenaltyType penaltyType;
        FeeBasis feeBasis;
        ContractPerformance creditEventTypeCovered;

        address currency;
        address settlementCurrency;

        bytes32 marketObjectCodeRateReset;

        // in the context of a Template date values are defined as offsets in seconds
        // respective to and anchorDate which is defined in the CustomTerms of the asset which references this template
        // if set == 0 offset is interpreted as a not set date value (see ZERO_OFFSET)
        uint256 statusDateOffset;
        uint256 maturityDateOffset;

        int256 notionalPrincipal;
        int256 nominalInterestRate;
        int256 feeAccrued;
        int256 accruedInterest;
        int256 rateMultiplier;
        int256 rateSpread;
        int256 feeRate;
        int256 nextResetRate;
        int256 penaltyRate;
        int256 premiumDiscountAtIED;
        int256 priceAtPurchaseDate;
        int256 nextPrincipalRedemptionPayment;
        int256 coverageOfCreditEnhancement;
        int256 lifeCap;
        int256 lifeFloor;
        int256 periodCap;
        int256 periodFloor;

        IP gracePeriod;
        IP delinquencyPeriod;
    }

    struct CustomTerms {
        uint256 anchorDate;
        // set of boolean values, true indicating that attribute is overwritten,
        // bit position from right to left in uint256 := position in lifecycleTerms (contractReference are always overwritten)
        // e.g. 0010110000...
        uint256 overwrittenAttributesMap;
        // terms object containing overwritten values
        LifecycleTerms overwrittenTerms;
    }
}

contracts/Core/ScheduleUtils.sol.content:
pragma solidity ^0.6.4;

import "./SharedTypes.sol";


contract ScheduleUtils is SharedTypes {

    function isUnscheduledEventType(EventType eventType)
        internal
        pure
        returns (bool)
    {
        if (eventType == EventType.CE || eventType == EventType.XD) {
            return true;
        }

        return false;
    }

    function isCyclicEventType(EventType eventType)
        internal
        pure
        returns (bool)
    {
        if (
            eventType == EventType.IP
            || eventType == EventType.IPCI
            || eventType == EventType.PR
            || eventType == EventType.SC
            || eventType == EventType.RR
            || eventType == EventType.PY
        ) {
            return true;
        }

        return false;
    }
}

