contracts/Core/AssetRegistry/AccessContr.content:
pragma solidity ^0.6.4;
pragma experimental ABIEncoderV2;

import "./AssetRegistryStorage.sol";
import "./IAssetRegistry.sol";


abstract contract AccessControl is AssetRegistryStorage, IAssetRegistry {

    event SetRootAccess(bytes32 indexed assetId, address indexed account);
    event GrantedAccess(bytes32 indexed assetId, address indexed account, bytes4 methodSignature);
    event RevokedAccess(bytes32 indexed assetId, address indexed account, bytes4 methodSignature);


    // Method signature == bytes4(0) := Access to all methods defined in the Asset Registry contract
    bytes4 constant ROOT_ACCESS = bytes4(0);


    /**
     * @notice Check whether an account is allowed to call a specific method on a specific asset.
     * @param assetId id of the asset
     * @param methodSignature function / method signature (4 byte keccak256 hash of the method selector)
     * @param account address of the account for which to check access
     * @return true if allowed access
     */
    function hasAccess (bytes32 assetId, bytes4 methodSignature, address account)
        public
        override
        returns (bool)
    {
        return (
            assets[assetId].access[methodSignature][account] || assets[assetId].access[ROOT_ACCESS][account]
        );
    }

    /**
     * @notice Check whether an account has root access for a specific asset.
     * @param assetId id of the asset
     * @param account address of the account for which to check root acccess
     * @return  true if has root access
     */
    function hasRootAccess (bytes32 assetId, address account)
        public
        override
        returns (bool)
    {
        return (assets[assetId].access[ROOT_ACCESS][account]);
    }

    /**
     * @notice Grant access to an account to call a specific method on a specific asset.
     * @dev Can only be called by an authorized account.
     * @param assetId id of the asset
     * @param methodSignature function / method signature (4 byte keccak256 hash of the method selector)
     * @param account address of the account to grant access to
     */
    function grantAccess (bytes32 assetId, bytes4 methodSignature, address account)
        public
        override
    {
        require(
            hasAccess(assetId, msg.sig, msg.sender),
            "AssetRegistry.revokeAccess: UNAUTHORIZED_SENDER"
        );

        assets[assetId].access[methodSignature][account] = true;

        emit GrantedAccess(assetId, account, methodSignature);
    }

    /**
     * @notice Revoke access for an account to call a specific method on a specific asset.
     * @dev Can only be called by an authorized account.
     * @param assetId id of the asset
     * @param methodSignature function / method signature (4 byte keccak256 hash of the method selector)
     * @param account address of the account to revoke access for
     */
    function revokeAccess (bytes32 assetId, bytes4 methodSignature, address account)
        public
        override
    {
        require(
            hasAccess(assetId, msg.sig, msg.sender),
            "AssetRegistry.revokeAccess: UNAUTHORIZED_SENDER"
        );

        assets[assetId].access[methodSignature][account] = false;

        emit RevokedAccess(assetId, account, methodSignature);
    }

    /**
     * @notice Grant access to an account to call all methods on a specific asset
     * (giving the account root access to an asset).
     * @param assetId id of the asset
     * @param account address of the account to set as the root
     */
    function setDefaultRoot (bytes32 assetId, address account) internal {
        assets[assetId].access[ROOT_ACCESS][account] = true;

        emit SetRootAccess(assetId, account);
    }
}

contracts/Core/AssetRegistry/AssetRegist.content:
pragma solidity ^0.6.4;
pragma experimental ABIEncoderV2;

import "./AssetRegistryStorage.sol";
import "./IAssetRegistry.sol";
import "./Economics.sol";
import "./Ownership.sol";


/**
 * @title AssetRegistry
 * @notice Registry for ACTUS Protocol assets
 */
contract AssetRegistry is AssetRegistryStorage, IAssetRegistry, Economics, Ownership {

    event RegisteredAsset(bytes32 assetId);


    constructor(ITemplateRegistry _templateRegistry)
        public
        AssetRegistryStorage(_templateRegistry)
    {}

    /**
     * @notice Returns if there is an asset registerd for a given assetId
     * @param assetId id of the asset
     * @return true if asset exist
     */
    function isRegistered(bytes32 assetId)
        external
        view
        override
        returns (bool)
    {
        return assets[assetId].isSet;
    }

    /**
     * @notice Stores the addresses of the owners (owner of creator-side payment obligations,
     * owner of creator-side payment claims), terms and the initial state of an asset
     * and sets the address of the actor (address of account which is allowed to update the state).
     * @dev The state of the asset can only be updates by a whitelisted actor.
     * @param assetId id of the asset
     * @param ownership ownership of the asset
     * @param templateId id of the financial template to use
     * @param terms asset specific terms (CustomTerms)
     * @param state initial state of the asset
     * @param engine ACTUS Engine of the asset
     * @param actor account which is allowed to update the asset state
     * @param admin account which as admin rights (optional)
     */
    function registerAsset(
        bytes32 assetId,
        AssetOwnership memory ownership,
        bytes32 templateId,
        CustomTerms memory terms,
        State memory state,
        address engine,
        address actor,
        address admin
    )
        public
        override
    {
        // revert if an asset with the specified assetId already exists
        require(
            assets[assetId].isSet == false,
            "AssetRegistry.registerAsset: ENTRY_ALREADY_EXISTS"
        );

        // store the asset
        setAsset(
            assetId,
            ownership,
            templateId,
            terms,
            state,
            engine,
            actor
        );

        // set external admin if specified
        if (admin != address(0)) {
            setDefaultRoot(assetId, admin);
        }

        emit RegisteredAsset(assetId);
    }
}

contracts/Core/AssetRegistry/Economics.s.content:
pragma solidity ^0.6.4;
pragma experimental ABIEncoderV2;

import "./AssetRegistryStorage.sol";
import "./AccessControl.sol";
import "./IAssetRegistry.sol";


/**
 * @title Economics
 */
abstract contract Economics is AssetRegistryStorage, IAssetRegistry, AccessControl {

    event IncrementedScheduleIndex(bytes32 indexed assetId, uint256 nextScheduleIndex);
    event UpdatedCustomTerms(bytes32 indexed assetId);
    event UpdatedState(bytes32 indexed assetId, uint256 statusDate);
    event UpdatedFinalizedState(bytes32 indexed assetId, uint256 statusDate);
    event UpdatedAnchorDate(bytes32 indexed assetId, uint256 prevAnchorDate, uint256 anchorDate);
    event UpdatedEngine(bytes32 indexed assetId, address prevEngine, address newEngine);
    event UpdatedActor(bytes32 indexed assetId, address prevActor, address newActor);


    modifier isAuthorized(bytes32 assetId) {
        require(
            msg.sender == assets[assetId].actor || hasAccess(assetId, msg.sig, msg.sender),
            "AssetRegistry.isAuthorized: UNAUTHORIZED_SENDER"
        );
        _;
    }

    /**
     * @notice Returns the terms of an asset.
     * @param assetId id of the asset
     * @return terms of the asset
     */
    function getTerms(bytes32 assetId)
        external
        view
        override
        returns (LifecycleTerms memory)
    {
        return decodeAndGetTerms(assetId);
    }

    /**
     * @notice Returns the state of an asset.
     * @param assetId id of the asset
     * @return state of the asset
     */
    function getState(bytes32 assetId)
        external
        view
        override
        returns (State memory)
    {
        return decodeAndGetState(assetId);
    }

    /**
     * @notice Returns the state of an asset.
     * @param assetId id of the asset
     * @return state of the asset
     */
    function getFinalizedState(bytes32 assetId)
        external
        view
        override
        returns (State memory)
    {
        return decodeAndGetFinalizedState(assetId);
    }

    /**
     * @notice Returns the anchor date of an asset.
     * @param assetId id of the asset
     * @return Anchor date
     */
    function getAnchorDate(bytes32 assetId)
        external
        view
        override
        returns (uint256)
    {
        return decodeAndGetAnchorDate(assetId);
    }

    /**
     * @notice Returns the address of a the ACTUS engine corresponding to the ContractType of an asset.
     * @param assetId id of the asset
     * @return address of the engine of the asset
     */
    function getEngine(bytes32 assetId)
        external
        view
        override
        returns (address)
    {
        return assets[assetId].engine;
    }

    /**
     * @notice Returns the address of the actor which is allowed to update the state of the asset.
     * @param assetId id of the asset
     * @return address of the asset actor
     */
    function getActor(bytes32 assetId)
        external
        view
        override
        returns (address)
    {
        return assets[assetId].actor;
    }

    /**
     * @notice Returns the id of the template which this asset is based on.
     * @param assetId id of the asset
     * @return id of the template
     */
    function getTemplateId(bytes32 assetId)
        external
        view
        override
        returns (bytes32)
    {
        return assets[assetId].templateId;
    }

    function getPendingEvent (bytes32 assetId)
        external
        view
        override
        returns (bytes32)
    {
        return assets[assetId].pendingEvent;
    }

    function pushPendingEvent (bytes32 assetId, bytes32 pendingEvent)
        external
        override
        isAuthorized (assetId)
    {
        assets[assetId].pendingEvent = pendingEvent;
    }

    function popPendingEvent (bytes32 assetId)
        external
        override
        isAuthorized (assetId)
        returns (bytes32)
    {
        bytes32 pendingEvent = assets[assetId].pendingEvent;
        assets[assetId].pendingEvent = bytes32(0);

        return pendingEvent;
    }

    /**
     * @notice If the underlying of the asset changes in performance to a covered performance,
     * it returns the exerciseDate event.
     */
    function getNextUnderlyingEvent (bytes32 assetId)
        external
        view
        override
        returns (bytes32)
    {
        LifecycleTerms memory terms = decodeAndGetTerms(assetId);
        State memory state = decodeAndGetState(assetId);

        // check for COVE
        if (
            terms.contractReference_1.object != bytes32(0)
            && terms.contractReference_1.role == ContractReferenceRole.COVE
        ) {
            bytes32 underlyingAssetId = terms.contractReference_1.object;
            State memory underlyingState = decodeAndGetState(underlyingAssetId);

            require(
                underlyingState.statusDate != uint256(0),
                "AssetActor.getNextObservedEvent: ENTRY_DOES_NOT_EXIST"
            );

            // check if exerciseDate has been triggered
            if (state.exerciseDate > 0) {
                // insert SettlementDate event
                return encodeEvent(
                    EventType.STD,
                    // solium-disable-next-line
                    block.timestamp
                );
            // if not check if performance of underlying asset is covered by this asset (PF excluded)
            } else if (
                terms.creditEventTypeCovered != ContractPerformance.PF
                && underlyingState.contractPerformance == terms.creditEventTypeCovered
            ) {
                // insert exerciseDate event
                // derive scheduleTimeOffset from performance
                if (underlyingState.contractPerformance == ContractPerformance.DL) {
                    return encodeEvent(
                        EventType.XD,
                        underlyingState.nonPerformingDate
                    );
                } else if (underlyingState.contractPerformance == ContractPerformance.DQ) {
                    LifecycleTerms memory underlyingTerms = decodeAndGetTerms(underlyingAssetId);
                    return encodeEvent(
                        EventType.XD,
                        getTimestampPlusPeriod(underlyingTerms.gracePeriod, underlyingState.nonPerformingDate)
                    );
                } else if (underlyingState.contractPerformance == ContractPerformance.DF) {
                    LifecycleTerms memory underlyingTerms = decodeAndGetTerms(underlyingAssetId);
                    return encodeEvent(
                        EventType.XD,
                        getTimestampPlusPeriod(underlyingTerms.delinquencyPeriod, underlyingState.nonPerformingDate)
                    );
                }
            }
        }

        return encodeEvent(EventType(0), 0);
    }


    /**
     * @notice Returns the index of the next event to be processed for a schedule of an asset.
     * @param assetId id of the asset
     * @return Index
     */
    function getNextScheduleIndex(bytes32 assetId)
        external
        view
        override
        returns (uint256)
    {
        return assets[assetId].nextScheduleIndex;
    }

    /**
     * @notice Returns the next event to process.
     * @param assetId id of the asset
     * @return event
     */
    function getNextScheduledEvent (bytes32 assetId)
        external
        view
        override
        returns (bytes32)
    {
        if (templateRegistry.getScheduleLength(assets[assetId].templateId) == 0) {
            return encodeEvent(EventType(0), 0);
        }

        bytes32 _event = templateRegistry.getEventAtIndex(
            assets[assetId].templateId,
            assets[assetId].nextScheduleIndex
        );
        (EventType nextEventType, uint256 nextScheduleTimeOffset) = decodeEvent(_event);

        return encodeEvent(
            nextEventType,
            applyAnchorDateToOffset(decodeAndGetAnchorDate(assetId), nextScheduleTimeOffset)
        );
    }

    /**
     * @notice Increments the index of a schedule of an asset.
     * (if max index is reached the index will be left unchanged)
     * @dev Can only be updated by the assets actor or by an authorized account.
     * @param assetId id of the asset
     */
    function popNextScheduledEvent(bytes32 assetId)
        external
        override
        isAuthorized (assetId)
        returns (bytes32)
    {
        if (assets[assetId].nextScheduleIndex == templateRegistry.getScheduleLength(assets[assetId].templateId)) {
            return encodeEvent(EventType(0), 0);
        }

        bytes32 _event = templateRegistry.getEventAtIndex(
            assets[assetId].templateId,
            assets[assetId].nextScheduleIndex
        );
        (EventType nextEventType, uint256 nextScheduleTimeOffset) = decodeEvent(_event);

        assets[assetId].nextScheduleIndex += 1;
        emit IncrementedScheduleIndex(assetId, assets[assetId].nextScheduleIndex);

        return encodeEvent(
            nextEventType,
            applyAnchorDateToOffset(decodeAndGetAnchorDate(assetId), nextScheduleTimeOffset)
        );
    }

    /**
     * @notice Set the custom terms of the asset
     * @dev Can only be set by authorized account.
     * @param assetId id of the asset
     * @param terms new CustomTerms
     */
    function setCustomTerms(bytes32 assetId, CustomTerms memory terms)
        public
        override
        isAuthorized (assetId)
    {
        encodeAndSetTerms(assetId, terms);

        emit UpdatedCustomTerms(assetId);
    }

    /**
     * @notice Sets next state of an asset.
     * @dev Can only be updated by the assets actor or by an authorized account.
     * @param assetId id of the asset
     * @param state next state of the asset
     */
    function setState(bytes32 assetId, State memory state)
        public
        override
        isAuthorized (assetId)
    {
        encodeAndSetState(assetId, state);

        emit UpdatedState(assetId, state.statusDate);
    }

    /**
     * @notice Sets next finalized state of an asset.
     * @dev Can only be updated by the assets actor or by an authorized account.
     * @param assetId id of the asset
     * @param state next state of the asset
     */
    function setFinalizedState(bytes32 assetId, State memory state)
        public
        override
        isAuthorized (assetId)
    {
        encodeAndSetFinalizedState(assetId, state);

        emit UpdatedFinalizedState(assetId, state.statusDate);
    }

    /**
     * @notice Set the anchor date which should used going forward to derive dates from the template
     * (used e.g. for pausing the asset).
     * @dev Can only be set by authorized account.
     * @param assetId id of the asset
     * @param anchorDate new anchor date of the asset
     */
    function setAnchorDate(bytes32 assetId, uint256 anchorDate)
        public
        override
        isAuthorized (assetId)
    {
        uint256 prevAnchorDate = decodeAndGetAnchorDate(assetId);

        encodeAndSetAnchorDate(assetId, anchorDate);

        emit UpdatedAnchorDate(assetId, prevAnchorDate, anchorDate);
    }

    /**
     * @notice Set the engine address which should be used for the asset going forward.
     * @dev Can only be set by authorized account.
     * @param assetId id of the asset
     * @param engine new engine address
     */
    function setEngine(bytes32 assetId, address engine)
        public
        override
        isAuthorized (assetId)
    {
        address prevEngine = assets[assetId].engine;

        assets[assetId].engine = engine;

        emit UpdatedEngine(assetId, prevEngine, engine);
    }

    /**
     * @notice Set the address of the Actor contract which should be going forward.
     * @param assetId id of the asset
     * @param actor address of the Actor contract
     */
    function setActor(bytes32 assetId, address actor)
        public
        override
        isAuthorized (assetId)
    {
        address prevActor = assets[assetId].actor;

        assets[assetId].actor = actor;

        emit UpdatedActor(assetId, prevActor, actor);
    }
}

contracts/Core/AssetRegistry/Ownership.s.content:
pragma solidity ^0.6.4;
pragma experimental ABIEncoderV2;

import "./AssetRegistryStorage.sol";
import "./AccessControl.sol";
import "./IAssetRegistry.sol";


/**
 * @title Ownership
 */
abstract contract Ownership is AssetRegistryStorage, IAssetRegistry, AccessControl {

    event UpdatedObligor (bytes32 assetId, address prevObligor, address newObligor);
    event UpdatedBeneficiary(bytes32 assetId, address prevBeneficiary, address newBeneficiary);
    event UpdatedCashflowBeneficiary(bytes32 assetId, int8 cashflowId, address prevBeneficiary, address newBeneficiary);


    /**
     * @notice Update the address of the default beneficiary of cashflows going to the creator.
     * @dev Can only be updated by the current creator beneficiary or by an authorized account.
     * @param assetId id of the asset
     * @param newCreatorBeneficiary address of the new beneficiary
     */
    function setCreatorBeneficiary(
        bytes32 assetId,
        address newCreatorBeneficiary
    )
        external
        override
    {
        address prevCreatorBeneficiary = assets[assetId].ownership.creatorBeneficiary;

        require(
            prevCreatorBeneficiary != address(0),
            "AssetRegistry.setCreatorBeneficiary: ENTRY_DOES_NOT_EXIST"
        );
        require(
            msg.sender == prevCreatorBeneficiary || hasAccess(assetId, msg.sig, msg.sender),
            "AssetRegistry.setCreatorBeneficiary: UNAUTHORIZED_SENDER"
        );

        assets[assetId].ownership.creatorBeneficiary = newCreatorBeneficiary;

        emit UpdatedBeneficiary(assetId, prevCreatorBeneficiary, newCreatorBeneficiary);
    }

    /**
     * @notice Updates the address of the default beneficiary of cashflows going to the counterparty.
     * @dev Can only be updated by the current counterparty beneficiary or by an authorized account.
     * @param assetId id of the asset
     * @param newCounterpartyBeneficiary address of the new beneficiary
     */
    function setCounterpartyBeneficiary(
        bytes32 assetId,
        address newCounterpartyBeneficiary
    )
        external
        override
    {
        address prevCounterpartyBeneficiary = assets[assetId].ownership.counterpartyBeneficiary;

        require(
            prevCounterpartyBeneficiary != address(0),
            "AssetRegistry.setCounterpartyBeneficiary: ENTRY_DOES_NOT_EXIST"
        );
        require(
            msg.sender == prevCounterpartyBeneficiary || hasAccess(assetId, msg.sig, msg.sender),
            "AssetRegistry.setCounterpartyBeneficiary: UNAUTHORIZED_SENDER"
        );

        assets[assetId].ownership.counterpartyBeneficiary = newCounterpartyBeneficiary;

        emit UpdatedBeneficiary(assetId, prevCounterpartyBeneficiary, newCounterpartyBeneficiary);
    }

    /**
     * @notice Registers the address of the owner of specific claims of the asset.
     * @dev Can only be updated by the current beneficiary or by an authorized account.
     * @param assetId id of the asset
     * @param cashflowId id of the specific claims for which to register the owner
     * @param beneficiary the address of the owner
     */
    function setBeneficiaryForCashflowId(
        bytes32 assetId,
        int8 cashflowId,
        address beneficiary
    )
        external
        override
    {
        require(
            cashflowId != 0,
            "AssetRegistry.setBeneficiaryForCashflowId: INVALID_CASHFLOWID"
        );

        address prevBeneficiary = assets[assetId].cashflowBeneficiaries[cashflowId];

        if (prevBeneficiary == address(0)) {
            if (cashflowId > 0) {
                require(
                    msg.sender == assets[assetId].ownership.creatorBeneficiary
                    || hasAccess(assetId, msg.sig, msg.sender),
                    "AssetRegistry.setBeneficiaryForCashflowId: UNAUTHORIZED_SENDER"
                );
            } else {
                require(
                    msg.sender == assets[assetId].ownership.counterpartyBeneficiary
                    || hasAccess(assetId, msg.sig, msg.sender),
                    "AssetRegistry.setBeneficiaryForCashflowId: UNAUTHORIZED_SENDER"
                );
            }
        } else {
            require(
                msg.sender == prevBeneficiary || hasAccess(assetId, msg.sig, msg.sender),
                "AssetRegistry.setBeneficiaryForCashflowId: UNAUTHORIZED_SENDER"
            );
        }

        assets[assetId].cashflowBeneficiaries[cashflowId] = beneficiary;

        emit UpdatedCashflowBeneficiary(
            assetId,
            cashflowId,
            prevBeneficiary,
            beneficiary
        );
    }

    /**
     * @notice Update the address of the obligor which has to fulfill obligations
     * for the creator of the asset.
     * @dev Can only be updated by an authorized account.
     * @param assetId id of the asset
     * @param newCreatorObligor address of the new creator obligor
     */
    function setCreatorObligor (bytes32 assetId, address newCreatorObligor)
        external
        override
    {
        require(
            hasAccess(assetId, msg.sig, msg.sender),
            "AssetRegistry.setCreatorObligor: UNAUTHORIZED_SENDER"
        );

        address prevCreatorObligor = assets[assetId].ownership.creatorObligor;

        assets[assetId].ownership.creatorObligor = newCreatorObligor;

        emit UpdatedObligor(assetId, prevCreatorObligor, newCreatorObligor);
    }

    /**
     * @notice Update the address of the counterparty which has to fulfill obligations
     * for the counterparty of the asset.
     * @dev Can only be updated by an authorized account.
     * @param assetId id of the asset
     * @param newCounterpartyObligor address of the new counterparty obligor
     */
    function setCounterpartyObligor (bytes32 assetId, address newCounterpartyObligor)
        external
        override
    {
        require(
            hasAccess(assetId, msg.sig, msg.sender),
            "AssetRegistry.setCounterpartyObligor: UNAUTHORIZED_SENDER"
        );

        address prevCounterpartyObligor = assets[assetId].ownership.counterpartyObligor;

        assets[assetId].ownership.counterpartyObligor = newCounterpartyObligor;

        emit UpdatedObligor(assetId, prevCounterpartyObligor, newCounterpartyObligor);
    }

    /**
     * @notice Retrieves the registered addresses of owners (creator, counterparty) of an asset.
     * @param assetId id of the asset
     * @return addresses of all owners of the asset
     */
    function getOwnership(bytes32 assetId)
        external
        view
        override
        returns (AssetOwnership memory)
    {
        return assets[assetId].ownership;
    }

    /**
     * @notice Retrieves the registered address of the owner of specific future claims from an asset.
     * @param assetId id of the asset
     * @param cashflowId the identifier of the specific claims owned by the registerd address
     * @return address of the beneficiary corresponding to the given cashflowId
     */
    function getCashflowBeneficiary(bytes32 assetId, int8 cashflowId)
        external
        view
        override
        returns (address)
    {
        return assets[assetId].cashflowBeneficiaries[cashflowId];
    }
}

contracts/Core/MarketObjectRegistry/Mark.content:
pragma solidity ^0.6.4;
pragma experimental ABIEncoderV2;

import "openzeppelin-solidity/contracts/ownership/Ownable.sol";

import "./IMarketObjectRegistry.sol";
import "./MarketObjectRegistryStorage.sol";


/**
 * @title MarketObjectRegistry
 * @notice Registry for data corresponding to a market object which is provided
 * by an registered MarketObjectProvider
 */
contract MarketObjectRegistry is MarketObjectRegistryStorage, IMarketObjectRegistry, Ownable {

    event UpdatedMarketObjectProvider(bytes32 indexed marketObjectId, address provider);

    event PublishedDataPoint(bytes32 indexed marketObjectId, int256 dataPoint);


    /**
     * @notice Registers / updates a market object provider.
     * @dev Can only be called by the owner of the MarketObjectRegistry.
     * @param marketObjectId id of the market object
     * @param provider address of the provider
     */
    function setMarketObjectProvider(
        bytes32 marketObjectId,
        address provider
    )
        public
        override
        onlyOwner
    {
        marketObjectProviders[marketObjectId] = provider;

        emit UpdatedMarketObjectProvider(marketObjectId, provider);
    }

    /**
     * @notice Stores a new data point of a market object for a given timestamp.
     * @dev Can only be called by a whitelisted market object provider.
     * @param marketObjectId id of the market object (see ACTUS spec.)
     * @param timestamp timestamp of the data point
     * @param dataPoint the data point for the market object
     */
    function publishDataPointOfMarketObject(
        bytes32 marketObjectId,
        uint256 timestamp,
        int256 dataPoint
    )
        public
        override
    {
        require(
            msg.sender == marketObjectProviders[marketObjectId],
            "MarketObjectRegistry.publishMarketObject: UNAUTHORIZED_SENDER"
        );

        dataPoints[marketObjectId][timestamp] = DataPoint(dataPoint, true);
        marketObjectLastUpdatedAt[marketObjectId] = timestamp;

        emit PublishedDataPoint(marketObjectId, dataPoint);
    }

    /**
     * @notice Returns a data point of a market object for a given timestamp.
     * @param marketObjectId id of the market object
     * @param timestamp timestamp of the data point
     * @return data point, bool indicating whether data point exists
     */
    function getDataPointOfMarketObject(
        bytes32 marketObjectId,
        uint256 timestamp
    )
        public
        view
        override
        returns (int256, bool)
    {
        return (
            dataPoints[marketObjectId][timestamp].dataPoint,
            dataPoints[marketObjectId][timestamp].isSet
        );
    }

    /**
     * @notice Returns the timestamp on which the last data point for a market object
     * was submitted.
     * @param marketObjectId id of the market object
     * @return last updated timestamp
     */
    function getMarketObjectLastUpdatedTimestamp(bytes32 marketObjectId)
        public
        view
        override
        returns (uint256)
    {
        return marketObjectLastUpdatedAt[marketObjectId];
    }
}

contracts/Core/TemplateRegistry/Template.content:
pragma solidity ^0.6.4;
pragma experimental ABIEncoderV2;

import "./ITemplateRegistry.sol";
import "./TemplateRegistryStorage.sol";


/**
 * @title TemplateRegistry
 * @notice Registry for ACTUS compatible templates.
 * A Template is made up of a set of TemplateTerms and TemplateSchedules.
 */
contract TemplateRegistry is TemplateRegistryStorage, ITemplateRegistry {

    event RegisteredTemplate(bytes32 indexed templateId);


    /**
     * @notice Returns true if there is a template registered for a given templateId
     * @param templateId id of the template
     * @return true if template exists
     */
    function isRegistered(bytes32 templateId)
        external
        view
        override
        returns (bool)
    {
        return templates[templateId].isSet;
    }

    /**
     * @notice Returns the terms of a template.
     * @param templateId id of the template
     * @return TemplateTerms
     */
    function getTemplateTerms(bytes32 templateId)
        external
        view
        override
        returns (TemplateTerms memory)
    {
        return (decodeAndGetTerms(templateId));
    }

    /**
     * @notice Returns an event for a given position (index) in a schedule of a given template.
     * @param templateId id of the template
     * @param index index of the event to return
     * @return Event
     */
    function getEventAtIndex(bytes32 templateId, uint256 index)
        external
        view
        override
        returns (bytes32)
    {
        return templates[templateId].templateSchedule.events[index];
    }

    /**
     * @notice Returns the length of a schedule of a given template.
     * @param templateId id of the template
     * @return Length of the schedule
     */
    function getScheduleLength(bytes32 templateId)
        external
        view
        override
        returns (uint256)
    {
        return templates[templateId].templateSchedule.length;
    }

    /**
     * @notice Convenience method for retrieving the entire schedule
     * Not recommended to execute method on-chain (if schedule is too long the tx may run out of gas)
     * @param templateId id of the template
     * @return the schedule
     */
    function getSchedule(bytes32 templateId)
        external
        view
        returns (bytes32[] memory)
    {
        uint256 scheduleLength = templates[templateId].templateSchedule.length;
        bytes32[] memory schedule = new bytes32[](scheduleLength);

        for (uint256 i = 0; i < scheduleLength; i++) {
            schedule[i] = templates[templateId].templateSchedule.events[i];
        }

        return schedule;
    }

    /**
     * @notice Stores a new template for given set of TemplateTerms and TemplateSchedules.
     * The templateId is derived from the hash of the TemplateTerms and the TemplateSchedules
     * to circumvent duplicate TemplateTerms on-chain.
     * @param terms set of TemplateTerms
     * @param templateSchedule templateSchedule which encodes offsets for ScheduleTime relative to an AnchorDate + EventType
     */
    function registerTemplate(TemplateTerms memory terms, bytes32[] memory templateSchedule)
        public
        override
    {
        // derive the templateId from the hash of the provided TemplateTerms and TemplateSchedule
        bytes32 templateId = keccak256(
            abi.encode(
                keccak256(abi.encode(terms)),
                keccak256(abi.encode(templateSchedule))
            )
        );

        // revert if a template for the derived template already exists
        require(
            templates[templateId].isSet == false,
            "TemplateRegistry.registerTemplate: ENTRY_ALREADY_EXISTS"
        );

        // store the template
        setTemplate(templateId, terms, templateSchedule);

        emit RegisteredTemplate(templateId);
    }
}

