pragma solidity ^0.7.0;

import "../../../external/BokkyPooBah/BokkyPooBahsDateTimeLibrary.sol";

import "../ACTUSTypes.sol";


/**
 * @title BusinessDayConventions
 * @notice Contains conventions of how to handle non-business days when generating schedules of events.
 * The events schedule time can be shifted or not, if shifted it is possible that it is shifted to the next
 * or previous valid business days, etc.
 */
contract BusinessDayConventions {

    /**
     * @notice Used in POFs and STFs for DCFs.
     * No shifting is applied if a Calc/Shift instead of Shift/Calc BDC is provided.
     */
    function shiftCalcTime(
        uint256 timestamp,
        BusinessDayConvention convention,
        Calendar calendar,
        uint256 maturityDate
    )
        public
        pure
        returns (uint256)
    {
        if (
            convention == BusinessDayConvention.CSF ||
            convention == BusinessDayConvention.CSMF ||
            convention == BusinessDayConvention.CSP ||
            convention == BusinessDayConvention.CSMP
        ) {
            return timestamp;
        }

        return shiftEventTime(timestamp, convention, calendar, maturityDate);
    }

    /*
     * @notice Used for generating event schedules (for single events and event cycles schedules).
     * This convention assumes that when shifting the events schedule time according
     * to a BDC, the time is shifted first and calculations are performed thereafter.
     * (Calculations in POFs and STFs are based on the shifted time as well)
     */
    function shiftEventTime(
        uint256 timestamp,
        BusinessDayConvention convention,
        Calendar calendar,
        uint256 maturityDate
    )
        public
        pure
        returns (uint256)
    {
        // do not shift if equal to maturity date
        if (timestamp == maturityDate) return timestamp;

        // Shift/Calc Following, Calc/Shift following
        if (convention == BusinessDayConvention.SCF || convention == BusinessDayConvention.CSF) {
            return getClosestBusinessDaySameDayOrFollowing(timestamp, calendar);
        // Shift/Calc Modified Following, Calc/Shift Modified following
        // Same as unmodified if shifted date is in the same month, if not it returns the previous buiness-day
        } else if (convention == BusinessDayConvention.SCMF || convention == BusinessDayConvention.CSMF) {
            uint256 followingOrSameBusinessDay = getClosestBusinessDaySameDayOrFollowing(timestamp, calendar);
            if (BokkyPooBahsDateTimeLibrary.getMonth(followingOrSameBusinessDay) == BokkyPooBahsDateTimeLibrary.getMonth(timestamp)) {
                return followingOrSameBusinessDay;
            }
            return getClosestBusinessDaySameDayOrPreceeding(timestamp, calendar);
        // Shift/Calc Preceeding, Calc/Shift Preceeding
        } else if (convention == BusinessDayConvention.SCP || convention == BusinessDayConvention.CSP) {
            return getClosestBusinessDaySameDayOrPreceeding(timestamp, calendar);
        // Shift/Calc Modified Preceeding, Calc/Shift Modified Preceeding
        // Same as unmodified if shifted date is in the same month, if not it returns the following buiness-day
        } else if (convention == BusinessDayConvention.SCMP || convention == BusinessDayConvention.CSMP) {
            uint256 preceedingOrSameBusinessDay = getClosestBusinessDaySameDayOrPreceeding(timestamp, calendar);
            if (BokkyPooBahsDateTimeLibrary.getMonth(preceedingOrSameBusinessDay) == BokkyPooBahsDateTimeLibrary.getMonth(timestamp)) {
                return preceedingOrSameBusinessDay;
            }
            return getClosestBusinessDaySameDayOrFollowing(timestamp, calendar);
        }

        return timestamp;
    }

    /**
     * @notice Returns the following business day if a non-business day is provided.
     * (Returns the same day if calendar != MondayToFriday)
     */
    function getClosestBusinessDaySameDayOrFollowing(uint256 timestamp, Calendar calendar)
        internal
        pure
        returns (uint256)
    {
        if (calendar == Calendar.MF) {
            if (BokkyPooBahsDateTimeLibrary.getDayOfWeek(timestamp) == 6) {
                return BokkyPooBahsDateTimeLibrary.addDays(timestamp, 2);
            } else if (BokkyPooBahsDateTimeLibrary.getDayOfWeek(timestamp) == 7) {
                return BokkyPooBahsDateTimeLibrary.addDays(timestamp, 1);
            }
        }
        return timestamp;
    }

    /**
     * @notice Returns the previous buiness day if a non-businessday is provided.
     * (Returns the same day if calendar != MondayToFriday)
     */
    function getClosestBusinessDaySameDayOrPreceeding(uint256 timestamp, Calendar calendar)
        internal
        pure
        returns (uint256)
    {
        if (calendar == Calendar.MF) {
            if (BokkyPooBahsDateTimeLibrary.getDayOfWeek(timestamp) == 6) {
                return BokkyPooBahsDateTimeLibrary.subDays(timestamp, 1);
            } else if (BokkyPooBahsDateTimeLibrary.getDayOfWeek(timestamp) == 7) {
                return BokkyPooBahsDateTimeLibrary.subDays(timestamp, 2);
            }
        }
        return timestamp;
    }
}
","keccak256":"0x1406e47e0631852d7cab09816927778609dd56893df469ecc7a492bb8b45aa67","license":"Apache-2.0"},"contracts/ACTUS/Core/SignedMath.sol":{"content":"// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;


/**
 * Advanced math library for signed integers
 * (including floats which are represented as multiples of 10 ** 18)
 */
library SignedMath {

    int256 constant private INT256_MIN = -2 ** 255;

    uint256 constant public PRECISION = 18;
    uint256 constant public MULTIPLICATOR = 10 ** PRECISION;


    /**
     * @dev The product of a and b has to be less than INT256_MAX (~10 ** 76),
     * as devision (normalization) is performed after multiplication
     * Upper boundary would be (10 ** 58) * (MULTIPLICATOR) == ~10 ** 76
     */
    function floatMult(int256 a, int256 b)
        internal
        pure
        returns (int256)
    {
        if (a == 0 || b == 0) return 0;

        require(!(a == -1 && b == INT256_MIN), "SignedMath.floatMult: OVERFLOW_DETECTED");
        int256 c = a * b;
        require(c / a == b, "SignedMath.floatMult: OVERFLOW_DETECTED");

        // normalize (divide by MULTIPLICATOR)
        int256 d = c / int256(MULTIPLICATOR);
        require(d != 0, "SignedMath.floatMult: CANNOT_REPRESENT_GRANULARITY");

        return d;
    }

    function floatDiv(int256 a, int256 b)
        internal
        pure
        returns (int256)
    {
        require(b != 0, "SignedMath.floatDiv: DIVIDED_BY_ZERO");

        // normalize (multiply by MULTIPLICATOR)
        if (a == 0) return 0;
        int256 c = a * int256(MULTIPLICATOR);
        require(c / a == int256(MULTIPLICATOR), "SignedMath.floatDiv: OVERFLOW_DETECTED");

        require(!(b == -1 && a == INT256_MIN), "SignedMath.floatDiv: OVERFLOW_DETECTED");
        int256 d = c / b;
        require(d != 0, "SignedMath.floatDiv: CANNOT_REPRESENT_GRANULARITY");

        return d;
    }

    /**
        * @dev Returns the smallest of two signed numbers.
        */
    function min(int256 a, int256 b) internal pure returns (int256) {
            return a <= b ? a : b;
    }

    /**
        * @dev Returns the largest of two signed numbers.
        */
    function max(int256 a, int256 b) internal pure returns (int256) {
            return a >= b ? a : b;
    }
}
","keccak256":"0x884bb3aec6597cb12c2ce3417ec60a1ffc82970342eefde5b195fad728efa233","license":"Apache-2.0"},"contracts/ACTUS/Core/Utils/EventUtils.sol":{"content":"// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "../ACTUSTypes.sol";

/**
 * @title EventUtils
 * @notice Methods for encoding decoding events
 */
contract EventUtils {

    function encodeEvent(EventType eventType, uint256 scheduleTime)
        public
        pure
        returns (bytes32)
    {
        return (
            bytes32(uint256(uint8(eventType))) << 248 |
            bytes32(scheduleTime)
        );
    }

    function decodeEvent(bytes32 _event)
        public
        pure
        returns (EventType, uint256)
    {
        EventType eventType = EventType(uint8(uint256(_event >> 248)));
        uint256 scheduleTime = uint256(uint64(uint256(_event)));

        return (eventType, scheduleTime);
    }

    /**
     * @notice Returns the epoch offset for a given event type to determine the
     * correct order of events if multiple events have the same timestamp
     */
    function getEpochOffset(EventType eventType)
        public
        pure
        returns (uint256)
    {
        return uint256(eventType);
    }
}
","keccak256":"0x0b57772142cc74943c55eea2bd2c8d2fd035bead19592b75cd4f7ad3159f96ee","license":"Apache-2.0"},"contracts/ACTUS/Engines/CEG/ICEGEngine.sol":{"content":"// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "../../Core/ACTUSTypes.sol";
import "../IEngine.sol";


/**
 * @title IEngine
 * @notice Interface which all Engines have to implement
 */
interface ICEGEngine is IEngine {

    /**
     * @notice Initialize contract state space based on the contract terms.
     * @param terms terms of the contract
     * @return initial state of the contract
     */
    function computeInitialState(CEGTerms calldata terms)
        external
        pure
        returns (State memory);

    /**
     * Applys an event to the current state of a contract and returns the resulting contract state.
     * @param terms terms of the contract
     * @param state current state of the contract
     * @param _event event to be applied to the contract state
     * @param externalData external data needed for STF evaluation (e.g. rate for RR events)
     * @return the resulting contract state
     */
    function computeStateForEvent(
        CEGTerms calldata terms,
        State calldata state,
        bytes32 _event,
        bytes32 externalData
    )
        external
        pure
        returns (State memory);

    /**
     * Evaluates the payoff for an event under the current state of the contract.
     * @param terms terms of the contract
     * @param state current state of the contract
     * @param _event event for which the payoff should be evaluated
     * @param externalData external data needed for POF evaluation (e.g. fxRate)
     * @return the payoff of the event
     */
    function computePayoffForEvent(
        CEGTerms calldata terms,
        State calldata state,
        bytes32 _event,
        bytes32 externalData
    )
        external
        pure
        returns (int256);

    /**
     * @notice Computes a schedule segment of non-cyclic contract events based on the contract terms
     * and the specified timestamps.
     * @param terms terms of the contract
     * @param segmentStart start timestamp of the segment
     * @param segmentEnd end timestamp of the segement
     * @return segment of the non-cyclic schedule
     */
    function computeNonCyclicScheduleSegment(
        CEGTerms calldata terms,
        uint256 segmentStart,
        uint256 segmentEnd
    )
        external
        pure
        returns (bytes32[] memory);

    /**
     * @notice Computes a schedule segment of cyclic contract events based on the contract terms
     * and the specified timestamps.
     * @param terms terms of the contract
     * @param segmentStart start timestamp of the segment
     * @param segmentEnd end timestamp of the segement
     * @param eventType eventType of the cyclic schedule
     * @return event schedule segment
     */
    function computeCyclicScheduleSegment(
        CEGTerms calldata terms,
        uint256 segmentStart,
        uint256 segmentEnd,
        EventType eventType
    )
        external
        pure
        returns (bytes32[] memory);

    /**
     * @notice Computes the next non-cyclic contract events based on the contract terms
     * and the timestamp on which the prev. event occured.
     * @dev Assumes that non-cyclic events of the same event type have a unique schedule time
     * @param terms terms of the contract
     * @param lastNonCyclicEvent last non-cyclic event
     * @return next non-cyclic event
     */
    function computeNextNonCyclicEvent(
        CEGTerms calldata terms,
        bytes32 lastNonCyclicEvent
    )
        external
        pure
        returns (bytes32);

    /**
     * @notice Computes a schedule segment of cyclic contract events based on the contract terms
     * and the specified timestamps.
     * @param terms terms of the contract
     * @param lastScheduleTime last occurrence of cyclic event
     * @param eventType eventType of the cyclic schedule
     * @return event schedule segment
     */
    function computeNextCyclicEvent(
        CEGTerms calldata terms,
        uint256 lastScheduleTime,
        EventType eventType
    )
        external
        pure
        returns(bytes32);

    /**
     * @notice Verifies that the provided event is still scheduled under the terms, the current state of the
     * contract and the current state of the underlying.
     * @param _event event for which to check if its still scheduled
     * @param terms terms of the contract
     * @param state current state of the contract
     * @param hasUnderlying boolean indicating whether the contract has an underlying contract
     * @param underlyingState state of the underlying (empty state object if non-existing)
     * @return boolean indicating whether event is still scheduled
     */
    function isEventScheduled(
        bytes32 _event,
        CEGTerms calldata terms,
        State calldata state,
        bool hasUnderlying,
        State calldata underlyingState
    )
        external
        pure
        returns (bool);
}
