// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;

import "../ACTUSTypes.sol";


/**
 * @title ContractRoleConventions
 */
contract ContractRoleConventions {

    /**
     * Returns the role sign for a given Contract Role.
     */
    function roleSign(ContractRole contractRole)
        internal
        pure
        returns (int8)
    {
        if (contractRole == ContractRole.RPA) return 1;
        if (contractRole == ContractRole.RPL) return -1;

        if (contractRole == ContractRole.BUY) return 1;
        if (contractRole == ContractRole.SEL) return -1;

        if (contractRole == ContractRole.RFL) return 1;
        if (contractRole == ContractRole.PFL) return -1;

        revert("ContractRoleConvention.roleSign: ATTRIBUTE_NOT_FOUND");
    }
}

contracts/ACTUS/Core/Conventions/DayCoun.content:
// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;

import "@openzeppelin/contracts/math/SafeMath.sol";
import "@openzeppelin/contracts/math/SignedSafeMath.sol";
import "../../../external/BokkyPooBah/BokkyPooBahsDateTimeLibrary.sol";

import "../ACTUSTypes.sol";
import "../SignedMath.sol";


/**
 * @title DayCountConventions
 * @notice Implements various ISDA day count conventions as specified by ACTUS
 */
contract DayCountConventions {

    using SafeMath for uint;
    using SignedSafeMath for int;
    using SignedMath for int;

    /**
     * Returns the fraction of the year between two timestamps.
     */
    function yearFraction(
        uint256 startTimestamp,
        uint256 endTimestamp,
        DayCountConvention ipdc,
        uint256 maturityDate
    )
        internal
        pure
        returns (int256)
    {
        require(endTimestamp >= startTimestamp, "Core.yearFraction: START_NOT_BEFORE_END");
        if (ipdc == DayCountConvention.AA) {
            return actualActual(startTimestamp, endTimestamp);
        } else if (ipdc == DayCountConvention.A360) {
            return actualThreeSixty(startTimestamp, endTimestamp);
        } else if (ipdc == DayCountConvention.A365) {
            return actualThreeSixtyFive(startTimestamp, endTimestamp);
        } else if (ipdc == DayCountConvention._30E360) {
            return thirtyEThreeSixty(startTimestamp, endTimestamp);
        } else if (ipdc == DayCountConvention._30E360ISDA) {
            return thirtyEThreeSixtyISDA(startTimestamp, endTimestamp, maturityDate);
        } else if (ipdc == DayCountConvention._28E336) {
            // not implemented yet
            revert("DayCountConvention.yearFraction: ATTRIBUTE_NOT_SUPPORTED.");
        } else {
            revert("DayCountConvention.yearFraction: ATTRIBUTE_NOT_FOUND.");
        }
    }

    /**
     * ISDA A/A day count convention
     */
    function actualActual(uint256 startTime, uint256 endTime)
        internal
        pure
        returns (int256)
    {
        uint256 d1Year = BokkyPooBahsDateTimeLibrary.getYear(startTime);
        uint256 d2Year = BokkyPooBahsDateTimeLibrary.getYear(endTime);

        int256 firstBasis = (BokkyPooBahsDateTimeLibrary.isLeapYear(startTime)) ? 366 : 365;

        if (d1Year == d2Year) {
            return int256(BokkyPooBahsDateTimeLibrary.diffDays(startTime, endTime)).floatDiv(firstBasis);
        }

        int256 secondBasis = (BokkyPooBahsDateTimeLibrary.isLeapYear(endTime)) ? 366 : 365;

        int256 firstFraction = int256(BokkyPooBahsDateTimeLibrary.diffDays(
            startTime,
            BokkyPooBahsDateTimeLibrary.timestampFromDate(d1Year.add(1), 1, 1)
        )).floatDiv(firstBasis);
        int256 secondFraction = int256(BokkyPooBahsDateTimeLibrary.diffDays(
            BokkyPooBahsDateTimeLibrary.timestampFromDate(d2Year, 1, 1),
            endTime
        )).floatDiv(secondBasis);

        return firstFraction.add(secondFraction).add(int256(d2Year.sub(d1Year).sub(1)));
    }

    /**
     * ISDA A/360 day count convention
     */
    function actualThreeSixty(uint256 startTime, uint256 endTime)
        internal
        pure
        returns (int256)
    {
        return (int256((endTime.sub(startTime)).div(86400)).floatDiv(360));
    }

    /**
     * ISDA A/365-Fixed day count convention
     */
    function actualThreeSixtyFive(uint256 startTime, uint256 endTime)
        internal
        pure
        returns (int256)
    {
        return (int256((endTime.sub(startTime)).div(86400)).floatDiv(365));
    }

    /**
     * ISDA 30E/360 day count convention
     */
    function thirtyEThreeSixty(uint256 startTime, uint256 endTime)
        internal
        pure
        returns (int256)
    {
        uint256 d1Day;
        uint256 d1Month;
        uint256 d1Year;

        uint256 d2Day;
        uint256 d2Month;
        uint256 d2Year;

        (d1Year, d1Month, d1Day) = BokkyPooBahsDateTimeLibrary.timestampToDate(startTime);
        (d2Year, d2Month, d2Day) = BokkyPooBahsDateTimeLibrary.timestampToDate(endTime);

        if (d1Day == 31) {
            d1Day = 30;
        }

        if (d2Day == 31) {
            d2Day = 30;
        }

        int256 delD = int256(d2Day).sub(int256(d1Day));
        int256 delM = int256(d2Month).sub(int256(d1Month));
        int256 delY = int256(d2Year).sub(int256(d1Year));

        return ((delY.mul(360).add(delM.mul(30)).add(delD)).floatDiv(360));
    }

    /**
     * ISDA 30E/360-ISDA day count convention
     */
    function thirtyEThreeSixtyISDA(uint256 startTime, uint256 endTime, uint256 maturityDate)
        internal
        pure
        returns (int256)
    {
        uint256 d1Day;
        uint256 d1Month;
        uint256 d1Year;

        uint256 d2Day;
        uint256 d2Month;
        uint256 d2Year;

        (d1Year, d1Month, d1Day) = BokkyPooBahsDateTimeLibrary.timestampToDate(startTime);
        (d2Year, d2Month, d2Day) = BokkyPooBahsDateTimeLibrary.timestampToDate(endTime);

        if (d1Day == BokkyPooBahsDateTimeLibrary.getDaysInMonth(startTime)) {
            d1Day = 30;
        }

        if (!(endTime == maturityDate && d2Month == 2) && d2Day == BokkyPooBahsDateTimeLibrary.getDaysInMonth(endTime)) {
            d2Day = 30;
        }

        int256 delD = int256(d2Day).sub(int256(d1Day));
        int256 delM = int256(d2Month).sub(int256(d1Month));
        int256 delY = int256(d2Year).sub(int256(d1Year));

        return ((delY.mul(360).add(delM.mul(30)).add(delD)).floatDiv(360));
    }
}

contracts/ACTUS/Core/Conventions/EndOfMo.content:
// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "../../../external/BokkyPooBah/BokkyPooBahsDateTimeLibrary.sol";

import "../ACTUSTypes.sol";


/**
 * @title EndOfMonthConventions
 * @notice Implements the ACTUS end of month convention.
 */
contract EndOfMonthConventions {

    /**
     * This function makes an adjustment on the end of month convention.
     * @dev The following is considered to dertermine if schedule dates are shifted to the end of month:
     * - The convention SD (same day) means not adjusting, EM (end of month) means adjusting
     * - Dates are only shifted if the schedule start date is an end-of-month date
     * - Dates are only shifted if the schedule cycle is based on an "M" period unit or multiple thereof
     * @param eomc the end of month convention to adjust
     * @param startTime timestamp of the cycle start
     * @param cycle the cycle struct
     * @return the adjusted end of month convention
     */
    function adjustEndOfMonthConvention(
        EndOfMonthConvention eomc,
        uint256 startTime,
        IPS memory cycle
    )
        public
        pure
        returns (EndOfMonthConvention)
    {
        if (eomc == EndOfMonthConvention.EOM) {
            // check if startTime is last day in month and schedule has month based period
            // otherwise switch to SD convention
            if (
                BokkyPooBahsDateTimeLibrary.getDay(startTime) == BokkyPooBahsDateTimeLibrary.getDaysInMonth(startTime) &&
                (cycle.p == P.M || cycle.p == P.Q || cycle.p == P.H)
            ) {
                return EndOfMonthConvention.EOM;
            }
            return EndOfMonthConvention.SD;
        } else if (eomc == EndOfMonthConvention.SD) {
            return EndOfMonthConvention.SD;
        }
        revert("EndOfMonthConvention.adjustEndOfMonthConvention: ATTRIBUTE_NOT_FOUND.");
    }

    /**
	 * This function is for the EndOfMonthConvention.EOM convention and
	 * shifts a timestamp to the last day of the month.
	 * @param timestamp the timestmap to shift
	 * @return the shifted timestamp
	 */
	function shiftEndOfMonth(uint256 timestamp)
	  internal
	  pure
	  returns (uint256)
	{
        // // check if startTime is last day in month and schedule has month based period
        // // otherwise switch to SD convention
        // if (
        //     eomc != EndOfMonthConvention.EOM
        //     || BokkyPooBahsDateTimeLibrary.getDay(startTime) != BokkyPooBahsDateTimeLibrary.getDaysInMonth(startTime)
        //     || (cycle.p == P.M || cycle.p == P.Q || cycle.p == P.H)
        // ) {
        //     // SD
        //     return timestamp;
        // }

		uint256 year;
		uint256 month;
		uint256 day;
		(year, month, day) = BokkyPooBahsDateTimeLibrary.timestampToDate(timestamp);
		uint256 lastDayOfMonth = BokkyPooBahsDateTimeLibrary._getDaysInMonth(year, month);

		return BokkyPooBahsDateTimeLibrary.timestampFromDate(year, month, lastDayOfMonth);
	}
}

contracts/ACTUS/Core/Utils/CycleUtils.so.content:
// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import {SafeMath as SafeMul} from "@openzeppelin/contracts/math/SafeMath.sol";
import "../../../external/BokkyPooBah/BokkyPooBahsDateTimeLibrary.sol";

import "../ACTUSTypes.sol";
import "../ACTUSConstants.sol";
import "../Conventions/EndOfMonthConventions.sol";
import "./PeriodUtils.sol";


/**
 * @title Schedule
 * @notice Methods related to generating event schedules.
 */
contract CycleUtils is ACTUSConstants, EndOfMonthConventions, PeriodUtils {

    using BokkyPooBahsDateTimeLibrary for uint;
    using SafeMul for uint;

    /**
     * @notice Applies the cycle n - times (n := cycleIndex) to a given date
     */
    function getNextCycleDate(IPS memory cycle, uint256 cycleStart, uint256 cycleIndex)
        internal
        pure
        returns (uint256)
    {
        uint256 newTimestamp;

        if (cycle.p == P.D) {
            newTimestamp = cycleStart.addDays(cycle.i.mul(cycleIndex));
        } else if (cycle.p == P.W) {
            newTimestamp = cycleStart.addDays(cycle.i.mul(7).mul(cycleIndex));
        } else if (cycle.p == P.M) {
            newTimestamp = cycleStart.addMonths(cycle.i.mul(cycleIndex));
        } else if (cycle.p == P.Q) {
            newTimestamp = cycleStart.addMonths(cycle.i.mul(3).mul(cycleIndex));
        } else if (cycle.p == P.H) {
            newTimestamp = cycleStart.addMonths(cycle.i.mul(6).mul(cycleIndex));
        } else if (cycle.p == P.Y) {
            newTimestamp = cycleStart.addYears(cycle.i.mul(cycleIndex));
        } else {
            revert("Schedule.getNextCycleDate: ATTRIBUTE_NOT_FOUND");
        }

        return newTimestamp;
    }

    /**
     * Computes an array of timestamps that represent dates in a cycle falling within a given segment.
     * @dev There are some notable edge cases: If the cycle is "not set" we return the start end end dates
     * of the cycle if they lie within the segment. Otherwise and empty array is returned.
     * @param cycleStart start time of the cycle
     * @param cycleEnd end time of the cycle
     * @param cycle IPS cycle
     * @param eomc end of month convention
     * @param addEndDate end date of the cycle should be added to the result if it falls in the segment
     * @param segmentStart start time of the segment
     * @param segmentEnd end time of the segment
     * @return an array of timestamps from the given cycle that fall within the specified segement
     */
    function computeDatesFromCycleSegment(
        uint256 cycleStart,
        uint256 cycleEnd,
        IPS memory cycle,
        EndOfMonthConvention eomc,
        bool addEndDate,
        uint256 segmentStart,
        uint256 segmentEnd
    )
        internal
        pure
        returns (uint256[MAX_CYCLE_SIZE] memory)
    {
        uint256[MAX_CYCLE_SIZE] memory dates;
        uint256 index;

        // if the cycle is not set we return only the cycle start end end dates under these conditions:
        // we return the cycle start, if it's in the segment
        // in case of addEntTime = true, the cycle end is also returned if in the segment
        if (cycle.isSet == false) {
            if (isInSegment(cycleStart, segmentStart, segmentEnd)) {
                dates[index] = cycleStart;
                index++;
            }
            if (isInSegment(cycleEnd, segmentStart, segmentEnd)) {
                if (addEndDate == true) dates[index] = cycleEnd;
            }
            return dates;
        }

        uint256 date = cycleStart;
        uint256 cycleIndex;

        EndOfMonthConvention actualEOMC = adjustEndOfMonthConvention(eomc, cycleStart, cycle);

        // walk through the cycle and create the cycle dates to be returned
        while (date < cycleEnd) {
            // if date is in segment and MAX_CYCLE_SIZE is not reached add it to the output array
            if (isInSegment(date, segmentStart, segmentEnd)) {
                require(index < (MAX_CYCLE_SIZE - 2), "Schedule.computeDatesFromCycle: MAX_CYCLE_SIZE");
                dates[index] = date;
                index++;
            }

            cycleIndex++;

            date = (actualEOMC == EndOfMonthConvention.EOM)
                ? shiftEndOfMonth(getNextCycleDate(cycle, cycleStart, cycleIndex))
                : getNextCycleDate(cycle, cycleStart, cycleIndex);
        }

        // add additional time at the end if addEndDate
        if (addEndDate == true) {
            if (isInSegment(cycleEnd, segmentStart, segmentEnd)) {
                dates[index] = cycleEnd;
            }
        }

        // handle a special case where S is set to LONG (e.g. for trimming a cycle to the maturity date)
        if (index > 0 && isInSegment(dates[index - 1], segmentStart, segmentEnd)) {
            if (cycle.s == S.LONG && index > 1 && cycleEnd != date) {
                dates[index - 1] = dates[index];
                delete dates[index];
            }
        }

        return dates;
    }

    /**
     * Computes the next date for a given an IPS cycle.
     * @param cycle IPS cycle
     * @param eomc end of month convention
     * @param anchorDate anchor date of the cycle
     * @param precedingDate the previous date of the cycle
     * @param addEndDate end date of the cycle should be added to the result if it falls in the segment
     * @param cycleEnd end date of the cyclic schedule
     * @return next date of the cycle
     */
    function computeNextCycleDateFromPrecedingDate(
        IPS memory cycle,
        EndOfMonthConvention eomc,
        uint256 anchorDate,
        uint256 precedingDate,
        bool addEndDate,
        uint256 cycleEnd
    )
        internal
        pure
        returns (uint256)
    {
        if (cycle.isSet == false || precedingDate == 0) return anchorDate;

        uint256 nextCyclicDate = (adjustEndOfMonthConvention(eomc, anchorDate, cycle) == EndOfMonthConvention.EOM)
            ? shiftEndOfMonth(getNextCycleDate(cycle, precedingDate, 1))
            : getNextCycleDate(cycle, precedingDate, 1);

        if (addEndDate && cycleEnd != 0) {
            // handle infinite loop (since preceding date is cycleEnd)
            if (precedingDate == cycleEnd) return 0;
            // add occurence at cycle end if date is greater than cycle end
            if (nextCyclicDate > cycleEnd) return cycleEnd;
        }
        // remove occurence if it is one cycle end
        if (addEndDate == false && cycleEnd != 0 && nextCyclicDate == cycleEnd) return 0;

        return nextCyclicDate;
    }

    /*
     * @notice Checks if a timestamp is in a given range.
     */
    function isInSegment(
        uint256 timestamp,
        uint256 startTimestamp,
        uint256 endTimestamp
    )
        internal
        pure
        returns (bool)
    {
        if (startTimestamp > endTimestamp) return false;
        if (startTimestamp <= timestamp && timestamp <= endTimestamp) return true;
        return false;
    }
}

