contracts/ACTUS/Engines/PAM/PAMEngine.so.content:
// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/math/SignedSafeMath.sol";

import "../../Core/Core.sol";
import "../../Core/SignedMath.sol";
import "./IPAMEngine.sol";
import "./PAMSTF.sol";
import "./PAMPOF.sol";


/**
 * @title PAMEngine
 * @notice Inherits from BaseEngine by implementing STFs, POFs according to the ACTUS standard for a PAM contract
 * @dev All numbers except unix timestamp are represented as multiple of 10 ** 18
 */
contract PAMEngine is Core, PAMSTF, PAMPOF, IPAMEngine {

    using SignedSafeMath for int;
    using SignedMath for int;


    function contractType() external pure override returns (ContractType) {
        return ContractType.PAM;
    }

    /**
     * Applys an event to the current state of a contract and returns the resulting contract state.
     * @param terms terms of the contract
     * @param state current state of the contract
     * @param _event event to be applied to the contract state
     * @param externalData external data needed for STF evaluation (e.g. rate for RR events)
     * @return the resulting contract state
     */
    function computeStateForEvent(
        PAMTerms calldata terms,
        State calldata state,
        bytes32 _event,
        bytes32 externalData
    )
        external
        pure
        override
        returns (State memory)
    {
        return stateTransitionFunction(
            terms,
            state,
            _event,
            externalData
        );
    }

    /**
     * Evaluates the payoff for an event under the current state of the contract.
     * @param terms terms of the contract
     * @param state current state of the contract
     * @param _event event for which the payoff should be evaluated
     * @param externalData external data needed for POF evaluation (e.g. fxRate)
     * @return the payoff of the event
     */
    function computePayoffForEvent(
        PAMTerms calldata terms,
        State calldata state,
        bytes32 _event,
        bytes32 externalData
    )
        external
        pure
        override
        returns (int256)
    {
        // if alternative settlementCurrency is set then apply fxRate to payoff
        if (terms.settlementCurrency != address(0) && terms.currency != terms.settlementCurrency) {
            return payoffFunction(
                terms,
                state,
                _event,
                externalData
            ).floatMult(int256(externalData));
        }

        return payoffFunction(
            terms,
            state,
            _event,
            externalData
        );
    }

    /**
     * @notice Initialize contract state space based on the contract terms.
     * @param terms terms of the contract
     * @return the initial state of the contract
     */
    function computeInitialState(PAMTerms calldata terms)
        external
        pure
        override
        returns (State memory)
    {
        State memory state;

        state.contractPerformance = ContractPerformance.PF;
        state.notionalScalingMultiplier = ONE_POINT_ZERO;
        state.interestScalingMultiplier = ONE_POINT_ZERO;
        state.statusDate = terms.statusDate;
        state.maturityDate = terms.maturityDate;
        state.notionalPrincipal = terms.notionalPrincipal;
        state.nominalInterestRate = terms.nominalInterestRate;
        state.accruedInterest = terms.accruedInterest;
        state.feeAccrued = terms.feeAccrued;

        return state;
    }

    /**
     * @notice Computes a schedule segment of non-cyclic contract events based on the contract terms
     * and the specified timestamps.
     * @param terms terms of the contract
     * @param segmentStart start timestamp of the segment
     * @param segmentEnd end timestamp of the segement
     * @return segment of the non-cyclic schedule
     */
    function computeNonCyclicScheduleSegment(
        PAMTerms calldata terms,
        uint256 segmentStart,
        uint256 segmentEnd
    )
        external
        pure
        override
        returns (bytes32[] memory)
    {
        bytes32[MAX_EVENT_SCHEDULE_SIZE] memory events;
        uint16 index;

        // initial exchange
        if (terms.purchaseDate == 0 && isInSegment(terms.initialExchangeDate, segmentStart, segmentEnd)) {
            events[index] = encodeEvent(EventType.IED, terms.initialExchangeDate);
            index++;
        }

        // purchase
        if (terms.purchaseDate != 0) {
            if (isInSegment(terms.purchaseDate, segmentStart, segmentEnd)) {
                events[index] = encodeEvent(EventType.PRD, terms.purchaseDate);
                index++;
            }
        }

        // principal redemption
        if (isInSegment(terms.maturityDate, segmentStart, segmentEnd)) {
            events[index] = encodeEvent(EventType.MD, terms.maturityDate);
            index++;
        }

        // remove null entries from returned array
        bytes32[] memory schedule = new bytes32[](index);
        for (uint256 i = 0; i < index; i++) {
            schedule[i] = events[i];
        }

        return schedule;
    }

    /**
     * @notice Computes a schedule segment of cyclic contract events based on the contract terms
     * and the specified timestamps.
     * @param terms terms of the contract
     * @param segmentStart start timestamp of the segment
     * @param segmentEnd end timestamp of the segement
     * @param eventType eventType of the cyclic schedule
     * @return event schedule segment
     */
    function computeCyclicScheduleSegment(
        PAMTerms calldata terms,
        uint256 segmentStart,
        uint256 segmentEnd,
        EventType eventType
    )
        external
        pure
        override
        returns(bytes32[] memory)
    {
        bytes32[MAX_EVENT_SCHEDULE_SIZE] memory events;
        uint256 index;

        // IP
        // interest payment related (starting with PRANX interest is paid following the PR schedule)
        if (eventType == EventType.IP) {
            if (terms.cycleAnchorDateOfInterestPayment != 0) {
                uint256[MAX_CYCLE_SIZE] memory interestPaymentSchedule = computeDatesFromCycleSegment(
                    terms.cycleAnchorDateOfInterestPayment,
                    terms.maturityDate,
                    terms.cycleOfInterestPayment,
                    terms.endOfMonthConvention,
                    true,
                    segmentStart,
                    segmentEnd
                );
                for (uint8 i = 0; i < MAX_CYCLE_SIZE; i++) {
                    if (interestPaymentSchedule[i] == 0) break;
                    if (interestPaymentSchedule[i] <= terms.capitalizationEndDate) continue;
                    if (isInSegment(interestPaymentSchedule[i], segmentStart, segmentEnd) == false) continue;
                    events[index] = encodeEvent(EventType.IP, interestPaymentSchedule[i]);
                    index++;
                }
            }
        }

        // IPCI
        if (eventType == EventType.IPCI) {
            if (terms.cycleAnchorDateOfInterestPayment != 0 && terms.capitalizationEndDate != 0) {
                IPS memory cycleOfInterestCapitalization = terms.cycleOfInterestPayment;
                cycleOfInterestCapitalization.s = S.SHORT;
                uint256[MAX_CYCLE_SIZE] memory interestPaymentSchedule = computeDatesFromCycleSegment(
                    terms.cycleAnchorDateOfInterestPayment,
                    terms.capitalizationEndDate,
                    cycleOfInterestCapitalization,
                    terms.endOfMonthConvention,
                    true,
                    segmentStart,
                    segmentEnd
                );
                for (uint8 i = 0; i < MAX_CYCLE_SIZE; i++) {
                    if (interestPaymentSchedule[i] == 0) break;
                    if (isInSegment(interestPaymentSchedule[i], segmentStart, segmentEnd) == false) continue;
                    events[index] = encodeEvent(EventType.IPCI, interestPaymentSchedule[i]);
                    index++;
                }
            }
        }

        // rate reset
        if (eventType == EventType.RR) {
            if (terms.cycleAnchorDateOfRateReset != 0) {
                uint256[MAX_CYCLE_SIZE] memory rateResetSchedule = computeDatesFromCycleSegment(
                    terms.cycleAnchorDateOfRateReset,
                    terms.maturityDate,
                    terms.cycleOfRateReset,
                    terms.endOfMonthConvention,
                    false,
                    segmentStart,
                    segmentEnd
                );
                for (uint8 i = 0; i < MAX_CYCLE_SIZE; i++) {
                    if (rateResetSchedule[i] == 0) break;
                    if (isInSegment(rateResetSchedule[i], segmentStart, segmentEnd) == false) continue;
                    events[index] = encodeEvent(EventType.RR, rateResetSchedule[i]);
                    index++;
                }
            }
            // ... nextRateReset
        }

        // fees
        if (eventType == EventType.FP) {
            if (terms.cycleAnchorDateOfFee != 0) {
                uint256[MAX_CYCLE_SIZE] memory feeSchedule = computeDatesFromCycleSegment(
                    terms.cycleAnchorDateOfFee,
                    terms.maturityDate,
                    terms.cycleOfFee,
                    terms.endOfMonthConvention,
                    true,
                    segmentStart,
                    segmentEnd
                );
                for (uint8 i = 0; i < MAX_CYCLE_SIZE; i++) {
                    if (feeSchedule[i] == 0) break;
                    if (isInSegment(feeSchedule[i], segmentStart, segmentEnd) == false) continue;
                    events[index] = encodeEvent(EventType.FP, feeSchedule[i]);
                    index++;
                }
            }
        }

        // scaling
        if (eventType == EventType.SC) {
            if ((terms.scalingEffect != ScalingEffect._000) && terms.cycleAnchorDateOfScalingIndex != 0) {
                uint256[MAX_CYCLE_SIZE] memory scalingSchedule = computeDatesFromCycleSegment(
                    terms.cycleAnchorDateOfScalingIndex,
                    terms.maturityDate,
                    terms.cycleOfScalingIndex,
                    terms.endOfMonthConvention,
                    true,
                    segmentStart,
                    segmentEnd
                );
                for (uint8 i = 0; i < MAX_CYCLE_SIZE; i++) {
                    if (scalingSchedule[i] == 0) break;
                    if (isInSegment(scalingSchedule[i], segmentStart, segmentEnd) == false) continue;
                    events[index] = encodeEvent(EventType.SC, scalingSchedule[i]);
                    index++;
                }
            }
        }

        // remove null entries from returned array
        bytes32[] memory schedule = new bytes32[](index);
        for (uint256 i = 0; i < index; i++) {
            schedule[i] = events[i];
        }

        return schedule;
    }

    /**
     * @notice Computes the next non-cyclic contract events based on the contract terms
     * and the timestamp on which the prev. event occured.
     * @dev Assumes that non-cyclic events of the same event type have a unique schedule time
     * @param terms terms of the contract
     * @param lastNonCyclicEvent last non-cyclic event
     * @return next non-cyclic event
     */
    function computeNextNonCyclicEvent(
        PAMTerms calldata terms,
        bytes32 lastNonCyclicEvent
    )
        external
        pure
        override
        returns (bytes32)
    {
        (EventType lastEventType, uint256 lastScheduleTime) = decodeEvent(lastNonCyclicEvent);

        EventType eventTypeNextEvent;
        uint256 scheduleTimeNextEvent;

        // EventTypes ordered after epoch offset - so we don't have make an additional epochOffset check

        // initial exchange
        if (
            // date for event has to be set in terms and date of event can be in the past
            (terms.initialExchangeDate != 0 && (lastScheduleTime <= terms.initialExchangeDate))
            // date for event has to come before previous candidate for the next event
            && (scheduleTimeNextEvent == 0 || terms.initialExchangeDate < scheduleTimeNextEvent)
            // avoid endless loop by requiring that the event is not the lastNonCyclicEvent
            && (lastScheduleTime != terms.initialExchangeDate || lastEventType != EventType.IED)
        ) {
            eventTypeNextEvent = EventType.IED;
            scheduleTimeNextEvent = terms.initialExchangeDate;
        }

        // purchase
        if (
            (terms.purchaseDate != 0 && (lastScheduleTime <= terms.purchaseDate))
            && (scheduleTimeNextEvent == 0 || terms.purchaseDate < scheduleTimeNextEvent)
            && (lastScheduleTime != terms.purchaseDate || lastEventType != EventType.PRD)
        ) {
            eventTypeNextEvent = EventType.PRD;
            scheduleTimeNextEvent = terms.purchaseDate;
        }

        // principal redemption at maturity
        if (
            (terms.maturityDate != 0 && (lastScheduleTime <= terms.maturityDate))
            && (scheduleTimeNextEvent == 0 || terms.maturityDate < scheduleTimeNextEvent)
            && (lastScheduleTime != terms.maturityDate || lastEventType != EventType.MD)
        ) {
            eventTypeNextEvent = EventType.MD;
            scheduleTimeNextEvent = terms.maturityDate;
        }

        return encodeEvent(eventTypeNextEvent, scheduleTimeNextEvent);
    }

    /**
     * @notice Computes a schedule segment of cyclic contract events based on the contract terms
     * and the specified timestamps.
     * @param terms terms of the contract
     * @param lastScheduleTime last occurrence of cyclic event
     * @param eventType eventType of the cyclic schedule
     * @return event schedule segment
     */
    function computeNextCyclicEvent(
        PAMTerms calldata terms,
        uint256 lastScheduleTime,
        EventType eventType
    )
        external
        pure
        override
        returns(bytes32)
    {
        // IP
        // interest payment related (starting with PRANX interest is paid following the PR schedule)
        if (eventType == EventType.IP) {
            if (terms.cycleOfInterestPayment.isSet == true && terms.cycleAnchorDateOfInterestPayment != 0) {
                uint256 nextInterestPaymentDate = computeNextCycleDateFromPrecedingDate(
                    terms.cycleOfInterestPayment,
                    terms.endOfMonthConvention,
                    terms.cycleAnchorDateOfInterestPayment,
                    lastScheduleTime,
                    true,
                    terms.maturityDate
                );
                if (nextInterestPaymentDate == 0) return bytes32(0);
                if (nextInterestPaymentDate <= terms.capitalizationEndDate) return bytes32(0);
                return encodeEvent(EventType.IP, nextInterestPaymentDate);
            }
        }

        // IPCI
        if (eventType == EventType.IPCI) {
            if (terms.cycleAnchorDateOfInterestPayment != 0 && terms.capitalizationEndDate != 0) {
                IPS memory cycleOfInterestCapitalization = terms.cycleOfInterestPayment;
                cycleOfInterestCapitalization.s = S.SHORT;
                uint256 nextInterestCapitalizationDate = computeNextCycleDateFromPrecedingDate(
                    cycleOfInterestCapitalization,
                    terms.endOfMonthConvention,
                    terms.cycleAnchorDateOfInterestPayment,
                    lastScheduleTime,
                    true,
                    terms.maturityDate
                );
                if (nextInterestCapitalizationDate == 0) return bytes32(0);
                return encodeEvent(EventType.IPCI, nextInterestCapitalizationDate);
            }
        }

        // rate reset
        if (eventType == EventType.RR) {
            if (terms.cycleAnchorDateOfRateReset != 0) {
                uint256 nextRateResetDate = computeNextCycleDateFromPrecedingDate(
                    terms.cycleOfRateReset,
                    terms.endOfMonthConvention,
                    terms.cycleAnchorDateOfRateReset,
                    lastScheduleTime,
                    true,
                    terms.maturityDate
                );
                if (nextRateResetDate == 0) return bytes32(0);
                return encodeEvent(EventType.RR, nextRateResetDate);
            }
            // ... nextRateReset
        }

        // fees
        if (eventType == EventType.FP) {
            if (terms.cycleAnchorDateOfFee != 0) {
                uint256 nextFeeDate = computeNextCycleDateFromPrecedingDate(
                    terms.cycleOfFee,
                    terms.endOfMonthConvention,
                    terms.cycleAnchorDateOfFee,
                    lastScheduleTime,
                    true,
                    terms.maturityDate
                );
                if (nextFeeDate == 0) return bytes32(0);
                return encodeEvent(EventType.FP, nextFeeDate);
            }
        }

        // scaling
        if (eventType == EventType.SC) {
            if ((terms.scalingEffect != ScalingEffect._000) && terms.cycleAnchorDateOfScalingIndex != 0) {
                uint256 nextScalingDate = computeNextCycleDateFromPrecedingDate(
                    terms.cycleOfScalingIndex,
                    terms.endOfMonthConvention,
                    terms.cycleAnchorDateOfScalingIndex,
                    lastScheduleTime,
                    true,
                    terms.maturityDate
                );
                if (nextScalingDate == 0) return bytes32(0);
                return encodeEvent(EventType.SC, nextScalingDate);
            }
        }

        return bytes32(0);
    }

    /**
     * @notice Verifies that the provided event is still scheduled under the terms, the current state of the
     * contract and the current state of the underlying.
     * param _event event for which to check if its still scheduled
     * param terms terms of the contract
     * param state current state of the contract
     * param hasUnderlying boolean indicating whether the contract has an underlying contract
     * param underlyingState state of the underlying (empty state object if non-existing)
     * @return boolean indicating whether event is still scheduled
     */
    function isEventScheduled(
        bytes32 /* _event */,
        PAMTerms calldata /* terms */,
        State calldata /* state */,
        bool /* hasUnderlying */,
        State calldata /* underlyingState */
    )
        external
        pure
        override
        returns (bool)
    {
        return true;
    }

    /**
     * @notice Implements abstract method which is defined in BaseEngine.
     * Applies an event to the current state of the contract and returns the resulting state.
     * The inheriting Engine contract has to map the events type to the designated STF.
     * todo Annuity calculator for RR/RRF events, IPCB events and ICB state variable
     * @param terms terms of the contract
     * @param state current state of the contract
     * @param _event event for which to evaluate the next state for
     * @param externalData external data needed for STF evaluation (e.g. rate for RR events)
     * @return the resulting contract state
     */
    function stateTransitionFunction(
        PAMTerms memory terms,
        State memory state,
        bytes32 _event,
        bytes32 externalData
    )
        internal
        pure
        returns (State memory)
    {
        (EventType eventType, uint256 scheduleTime) = decodeEvent(_event);

        /*
         * Note:
         * Not supported: PRD (Purchase) events
         */

        if (eventType == EventType.AD) return STF_PAM_AD(terms, state, scheduleTime, externalData);
        if (eventType == EventType.FP) return STF_PAM_FP(terms, state, scheduleTime, externalData);
        if (eventType == EventType.IED) return STF_PAM_IED(terms, state, scheduleTime, externalData);
        if (eventType == EventType.IPCI) return STF_PAM_IPCI(terms, state, scheduleTime, externalData);
        if (eventType == EventType.IP) return STF_PAM_IP(terms, state, scheduleTime, externalData);
        if (eventType == EventType.PP) return STF_PAM_PP(terms, state, scheduleTime, externalData);
        if (eventType == EventType.MD) return STF_PAM_MD(terms, state, scheduleTime, externalData);
        if (eventType == EventType.PY) return STF_PAM_PY(terms, state, scheduleTime, externalData);
        if (eventType == EventType.RRF) return STF_PAM_RRF(terms, state, scheduleTime, externalData);
        if (eventType == EventType.RR) return STF_PAM_RR(terms, state, scheduleTime, externalData);
        if (eventType == EventType.SC) return STF_PAM_SC(terms, state, scheduleTime, externalData);
        if (eventType == EventType.TD) return STF_PAM_TD(terms, state, scheduleTime, externalData);
        if (eventType == EventType.CE)  return STF_PAM_CE(terms, state, scheduleTime, externalData);

        revert("PAMEngine.stateTransitionFunction: ATTRIBUTE_NOT_FOUND");
    }

    /**
     * @notice Implements abstract method which is defined in BaseEngine.
     * Computes the payoff for an event under the current state of the contract.
     * The inheriting Engine contract has to map the events type to the designated POF.
     * todo IPCB events and Icb state variable, Icb state variable updates in IP-paying events
     * @param terms terms of the contract
     * @param state current state of the contract
     * @param _event event for which the payoff should be evaluated
     * @param externalData external data needed for POF evaluation (e.g. fxRate)
     * @return the payoff of the event
     */
    function payoffFunction(
        PAMTerms memory terms,
        State memory state,
        bytes32 _event,
        bytes32 externalData
    )
        internal
        pure
        returns (int256)
    {
        (EventType eventType, uint256 scheduleTime) = decodeEvent(_event);

        /*
         * Note: PAM contracts don't have IPCB and PR events.
         * Not supported: PRD (Purchase) events
         */

        if (eventType == EventType.AD) return 0;
        if (eventType == EventType.IPCI) return 0;
        if (eventType == EventType.RRF) return 0;
        if (eventType == EventType.RR) return 0;
        if (eventType == EventType.SC) return 0;
        if (eventType == EventType.CE) return 0;
        if (eventType == EventType.FP) return POF_PAM_FP(terms, state, scheduleTime, externalData);
        if (eventType == EventType.IED) return POF_PAM_IED(terms, state, scheduleTime, externalData);
        if (eventType == EventType.IP) return POF_PAM_IP(terms, state, scheduleTime, externalData);
        if (eventType == EventType.PP) return POF_PAM_PP(terms, state, scheduleTime, externalData);
        if (eventType == EventType.MD) return POF_PAM_MD(terms, state, scheduleTime, externalData);
        if (eventType == EventType.PY) return POF_PAM_PY(terms, state, scheduleTime, externalData);
        if (eventType == EventType.TD) return POF_PAM_TD(terms, state, scheduleTime, externalData);

        revert("PAMEngine.payoffFunction: ATTRIBUTE_NOT_FOUND");
    }
}

contracts/Core/CERTF/CERTFActor.sol.content:
// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "../../ACTUS/Engines/CERTF/ICERTFEngine.sol";

import "../Base/AssetActor/BaseActor.sol";
import "./ICERTFRegistry.sol";


/**
 * @title CERTFActor
 * @notice TODO
 */
contract CERTFActor is BaseActor {

    using SignedMath for int;


    constructor(IAssetRegistry assetRegistry, IOracleRegistry oracleRegistry) BaseActor(assetRegistry, oracleRegistry) {}

    /**
     * @notice Derives initial state of the asset terms and stores together with
     * terms, schedule, ownership, engine, admin of the asset in the contract types specific AssetRegistry.
     * @param terms asset specific terms
     * @param schedule schedule of the asset
     * @param ownership ownership of the asset
     * @param engine address of the ACTUS engine used for the spec. ContractType
     * @param admin address of the admin of the asset (optional)
     */
    function initialize(
        CERTFTerms calldata terms,
        bytes32[] calldata schedule,
        AssetOwnership calldata ownership,
        address engine,
        address admin
    )
        external
    {
        require(
            engine != address(0) && IEngine(engine).contractType() == ContractType.CERTF,
            "CERTFActor.initialize: CONTRACT_TYPE_OF_ENGINE_UNSUPPORTED"
        );

        // solium-disable-next-line
        bytes32 assetId = keccak256(abi.encode(terms, block.timestamp));

        // compute the initial state of the asset
        State memory initialState = ICERTFEngine(engine).computeInitialState(terms);

        // register the asset in the AssetRegistry
        ICERTFRegistry(address(assetRegistry)).registerAsset(
            assetId,
            terms,
            initialState,
            schedule,
            ownership,
            engine,
            address(this),
            admin
        );

        emit InitializedAsset(assetId, ContractType.CEG, ownership.creatorObligor, ownership.counterpartyObligor);
    }

    function computeStateAndPayoffForEvent(bytes32 assetId, State memory state, bytes32 _event)
        internal
        view
        override
        returns (State memory, int256)
    {
        address engine = assetRegistry.getEngine(assetId);
        CERTFTerms memory terms = ICERTFRegistry(address(assetRegistry)).getTerms(assetId);
        (EventType eventType, uint256 scheduleTime) = decodeEvent(_event);

        int256 payoff = ICERTFEngine(engine).computePayoffForEvent(
            terms,
            state,
            _event,
            getExternalDataForPOF(
                assetId,
                eventType,
                shiftCalcTime(scheduleTime, terms.businessDayConvention, terms.calendar, terms.maturityDate)
            )
        );
        state = ICERTFEngine(engine).computeStateForEvent(
            terms,
            state,
            _event,
            getExternalDataForSTF(
                assetId,
                eventType,
                shiftCalcTime(scheduleTime, terms.businessDayConvention, terms.calendar, terms.maturityDate)
            )
        );

        return (state, payoff);
    }

    /**
     * @notice Retrieves external data (such as market object data, block time, underlying asset state)
     * used for evaluating the STF for a given event.
     */
    function getExternalDataForSTF(
        bytes32 assetId,
        EventType eventType,
        uint256 timestamp
    )
        internal
        view
        override
        returns (bytes32)
    {
        if (eventType == EventType.CE) {
            // get current timestamp
            // solium-disable-next-line
            return bytes32(block.timestamp);
        } else if (eventType == EventType.EXE) {
            // get quantity
            ContractReference memory contractReference_2 = assetRegistry.getContractReferenceValueForTermsAttribute(
                assetId,
                "contractReference_2"
            );
            if (
                contractReference_2._type == ContractReferenceType.MOC
                && contractReference_2.role == ContractReferenceRole.UDL
            ) {
                (int256 quantity, bool isSet) = oracleRegistry.getDataPoint(
                    address(0),
                    abi.encode(contractReference_2.object,timestamp)
                );
                if (isSet) return bytes32(quantity);
            }
        } else if (eventType == EventType.REF) {
            ContractReference memory contractReference_1 = assetRegistry.getContractReferenceValueForTermsAttribute(
                assetId,
                "contractReference_1"
            );
            if (
                contractReference_1._type == ContractReferenceType.MOC
                && contractReference_1.role == ContractReferenceRole.UDL
            ) {
                (int256 marketValueScheduleTime, bool isSetScheduleTime) = oracleRegistry.getDataPoint(
                    address(0),
                    abi.encode(contractReference_1.object,timestamp)
                );
                (int256 marketValueAnchorDate, bool isSetAnchorDate) = oracleRegistry.getDataPoint(
                    address(0),
                    abi.encode(contractReference_1.object,assetRegistry.getUIntValueForTermsAttribute(assetId, "issueDate"))
                );
                if (isSetScheduleTime && isSetAnchorDate) {
                    return bytes32(marketValueScheduleTime.floatDiv(marketValueAnchorDate));
                }
            }
            return bytes32(0);
        }

        return bytes32(0);
    }
}

contracts/ACTUS/Engines/CERTF/CERTFEngin.content:
// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/math/SignedSafeMath.sol";

import "../../Core/Core.sol";
import "../../Core/SignedMath.sol";
import "./ICERTFEngine.sol";
import "./CERTFSTF.sol";
import "./CERTFPOF.sol";


/**
 * @title CERTFEngine
 * @notice Inherits from BaseEngine by implementing STFs, POFs according to the ACTUS standard for a CERTF contract
 * @dev All numbers except unix timestamp are represented as multiple of 10 ** 18
 */
contract CERTFEngine is Core, CERTFSTF, CERTFPOF, ICERTFEngine {

    using SignedSafeMath for int;
    using SignedMath for int;


    function contractType() external pure override returns (ContractType) {
        return ContractType.CERTF;
    }

    /**
     * Applys an event to the current state of a contract and returns the resulting contract state.
     * @param terms terms of the contract
     * @param state current state of the contract
     * @param _event event to be applied to the contract state
     * @param externalData external data needed for STF evaluation (e.g. rate for RR events)
     * @return the resulting contract state
     */
    function computeStateForEvent(
        CERTFTerms calldata terms,
        State calldata state,
        bytes32 _event,
        bytes32 externalData
    )
        external
        pure
        override
        returns (State memory)
    {
        return stateTransitionFunction(
            terms,
            state,
            _event,
            externalData
        );
    }

    /**
     * Evaluates the payoff for an event under the current state of the contract.
     * @param terms terms of the contract
     * @param state current state of the contract
     * @param _event event for which the payoff should be evaluated
     * @param externalData external data needed for POF evaluation (e.g. fxRate)
     * @return the payoff of the event
     */
    function computePayoffForEvent(
        CERTFTerms calldata terms,
        State calldata state,
        bytes32 _event,
        bytes32 externalData
    )
        external
        pure
        override
        returns (int256)
    {
        // if alternative settlementCurrency is set then apply fxRate to payoff
        if (terms.settlementCurrency != address(0) && terms.currency != terms.settlementCurrency) {
            return payoffFunction(
                terms,
                state,
                _event,
                externalData
            ).floatMult(int256(externalData));
        }

        return payoffFunction(
            terms,
            state,
            _event,
            externalData
        );
    }

    /**
     * @notice Initialize contract state space based on the contract terms.
     * @param terms terms of the contract
     * @return the initial state of the contract
     */
    function computeInitialState(CERTFTerms calldata terms)
        external
        pure
        override
        returns (State memory)
    {
        State memory state;

        state.quantity = 0;
        state.exerciseQuantity = 0;
        state.marginFactor = ONE_POINT_ZERO;
        state.adjustmentFactor = ONE_POINT_ZERO;
        state.lastCouponFixingDate = terms.issueDate;
        state.couponAmountFixed = 0;

        state.contractPerformance = ContractPerformance.PF;
        state.statusDate = terms.statusDate;

        return state;
    }

    /**
     * @notice Computes a schedule segment of non-cyclic contract events based on the contract terms
     * and the specified timestamps.
     * @param terms terms of the contract
     * @param segmentStart start timestamp of the segment
     * @param segmentEnd end timestamp of the segement
     * @return segment of the non-cyclic schedule
     */
    function computeNonCyclicScheduleSegment(
        CERTFTerms calldata terms,
        uint256 segmentStart,
        uint256 segmentEnd
    )
        external
        pure
        override
        returns (bytes32[] memory)
    {
        bytes32[MAX_EVENT_SCHEDULE_SIZE] memory events;
        uint16 index;

        // issue date
        if (terms.issueDate != 0) {
            if (isInSegment(terms.issueDate, segmentStart, segmentEnd)) {
                events[index] = encodeEvent(EventType.ISS, terms.issueDate);
                index++;
            }
        }

        // initial exchange
        if (terms.initialExchangeDate != 0) {
            if (isInSegment(terms.initialExchangeDate, segmentStart, segmentEnd)) {
                events[index] = encodeEvent(EventType.IED, terms.initialExchangeDate);
                index++;
            }
        }

        // maturity event
        if (terms.maturityDate != 0) {
            if (isInSegment(terms.maturityDate, segmentStart, segmentEnd) == true) {
                events[index] = encodeEvent(EventType.MD, terms.maturityDate);
                index++;
            }
        }

        // remove null entries from returned array
        bytes32[] memory schedule = new bytes32[](index);
        for (uint256 i = 0; i < index; i++) {
            schedule[i] = events[i];
        }

        return schedule;
    }

    /**
     * @notice Computes a schedule segment of cyclic contract events based on the contract terms
     * and the specified timestamps.
     * @param terms terms of the contract
     * @param segmentStart start timestamp of the segment
     * @param segmentEnd end timestamp of the segement
     * @param eventType eventType of the cyclic schedule
     * @return event schedule segment
     */
    function computeCyclicScheduleSegment(
        CERTFTerms calldata terms,
        uint256 segmentStart,
        uint256 segmentEnd,
        EventType eventType
    )
        external
        pure
        override
        returns(bytes32[] memory)
    {
        bytes32[MAX_EVENT_SCHEDULE_SIZE] memory events;
        uint256 index;

        if (eventType == EventType.COF) {
            if (terms.cycleAnchorDateOfCoupon != 0) {
                uint256[MAX_CYCLE_SIZE] memory couponSchedule = computeDatesFromCycleSegment(
                    terms.cycleAnchorDateOfCoupon,
                    (terms.maturityDate > 0) ? terms.maturityDate : segmentEnd,
                    terms.cycleOfCoupon,
                    terms.endOfMonthConvention,
                    (terms.maturityDate > 0) ? true : false,
                    segmentStart,
                    segmentEnd
                );
                for (uint8 i = 0; i < MAX_CYCLE_SIZE; i++) {
                    if (couponSchedule[i] == 0) break;
                    if (isInSegment(couponSchedule[i], segmentStart, segmentEnd) == false) continue;
                    events[index] = encodeEvent(EventType.COF, couponSchedule[i]);
                    index++;
                }
            }
        }

         if (eventType == EventType.COP) {
            if (terms.cycleAnchorDateOfCoupon != 0) {
                uint256[MAX_CYCLE_SIZE] memory couponSchedule = computeDatesFromCycleSegment(
                    terms.cycleAnchorDateOfCoupon,
                    (terms.maturityDate > 0) ? terms.maturityDate : segmentEnd,
                    terms.cycleOfCoupon,
                    terms.endOfMonthConvention,
                    (terms.maturityDate > 0) ? true : false,
                    segmentStart,
                    segmentEnd
                );
                for (uint8 i = 0; i < MAX_CYCLE_SIZE; i++) {
                    if (couponSchedule[i] == 0) break;
                    uint256 couponPaymentDayScheduleTime = getTimestampPlusPeriod(terms.settlementPeriod, couponSchedule[i]);
                    if (isInSegment(couponPaymentDayScheduleTime, segmentStart, segmentEnd) == false) continue;
                    events[index] = encodeEvent(EventType.COF, couponPaymentDayScheduleTime);
                    index++;
                }
            }
        }

        if (eventType == EventType.REF) {
            if (terms.cycleAnchorDateOfRedemption != 0) {
                uint256[MAX_CYCLE_SIZE] memory redemptionSchedule = computeDatesFromCycleSegment(
                    terms.cycleAnchorDateOfRedemption,
                    (terms.maturityDate > 0) ? terms.maturityDate : segmentEnd,
                    terms.cycleOfRedemption,
                    terms.endOfMonthConvention,
                    (terms.maturityDate > 0) ? true : false,
                    segmentStart,
                    segmentEnd
                );
                for (uint8 i = 0; i < MAX_CYCLE_SIZE; i++) {
                    if (redemptionSchedule[i] == 0) break;
                    if (isInSegment(redemptionSchedule[i], segmentStart, segmentEnd) == false) continue;
                    events[index] = encodeEvent(EventType.REF, redemptionSchedule[i]);
                    index++;
                }
            }
        }

        if (eventType == EventType.REP) {
            if (terms.cycleAnchorDateOfRedemption != 0) {
                uint256[MAX_CYCLE_SIZE] memory redemptionSchedule = computeDatesFromCycleSegment(
                    terms.cycleAnchorDateOfRedemption,
                    (terms.maturityDate > 0) ? terms.maturityDate : segmentEnd,
                    terms.cycleOfRedemption,
                    terms.endOfMonthConvention,
                    (terms.maturityDate > 0) ? true : false,
                    segmentStart,
                    segmentEnd
                );
                for (uint8 i = 0; i < MAX_CYCLE_SIZE; i++) {
                    if (redemptionSchedule[i] == 0) break;
                    uint256 redemptionPaymentDayScheduleTime = getTimestampPlusPeriod(terms.settlementPeriod, redemptionSchedule[i]);
                    if (isInSegment(redemptionPaymentDayScheduleTime, segmentStart, segmentEnd) == false) continue;
                    events[index] = encodeEvent(EventType.REP, redemptionPaymentDayScheduleTime);
                    index++;
                }
            }
        }

        if (eventType == EventType.EXE) {
            if (terms.cycleAnchorDateOfRedemption != 0) {
                uint256[MAX_CYCLE_SIZE] memory redemptionSchedule = computeDatesFromCycleSegment(
                    terms.cycleAnchorDateOfRedemption,
                    (terms.maturityDate > 0) ? terms.maturityDate : segmentEnd,
                    terms.cycleOfRedemption,
                    terms.endOfMonthConvention,
                    (terms.maturityDate > 0) ? true : false,
                    segmentStart,
                    segmentEnd
                );
                for (uint8 i = 0; i < MAX_CYCLE_SIZE; i++) {
                    if (redemptionSchedule[i] == 0) break;
                    if (redemptionSchedule[i] == terms.maturityDate) continue;
                    uint256 executionDateScheduleTime = getTimestampPlusPeriod(terms.redemptionExercisePeriod, redemptionSchedule[i]);
                    if (isInSegment(executionDateScheduleTime, segmentStart, segmentEnd) == false) continue;
                    events[index] = encodeEvent(EventType.EXE, executionDateScheduleTime);
                    index++;
                }
            }
        }

        // remove null entries from returned array
        bytes32[] memory schedule = new bytes32[](index);
        for (uint256 i = 0; i < index; i++) {
            schedule[i] = events[i];
        }

        return schedule;
    }

    /**
     * @notice Computes the next non-cyclic contract events based on the contract terms
     * and the timestamp on which the prev. event occured.
     * @dev Assumes that non-cyclic events of the same event type have a unique schedule time
     * @param terms terms of the contract
     * @param lastNonCyclicEvent last non-cyclic event
     * @return next non-cyclic event
     */
    function computeNextNonCyclicEvent(
        CERTFTerms calldata terms,
        bytes32 lastNonCyclicEvent
    )
        external
        pure
        override
        returns (bytes32)
    {
        (EventType lastEventType, uint256 lastScheduleTime) = decodeEvent(lastNonCyclicEvent);

        EventType eventTypeNextEvent;
        uint256 scheduleTimeNextEvent;

        // EventTypes ordered after epoch offset - so we don't have make an additional epochOffset check

        // issue date
        if (
            // date for event has to be set in terms and date of event can be in the past
            (terms.issueDate != 0 && (lastScheduleTime <= terms.issueDate))
            // date for event has to come before previous candidate for the next event
            && (scheduleTimeNextEvent == 0 || terms.issueDate < scheduleTimeNextEvent)
            // avoid endless loop by requiring that the event is not the lastNonCyclicEvent
            && (lastScheduleTime != terms.issueDate || lastEventType != EventType.ISS)
        ) {
            eventTypeNextEvent = EventType.ISS;
            scheduleTimeNextEvent = terms.issueDate;
        }

        // initial exchange
        if (
            (terms.initialExchangeDate != 0 && (lastScheduleTime <= terms.initialExchangeDate))
            && (scheduleTimeNextEvent == 0 || terms.initialExchangeDate < scheduleTimeNextEvent)
            && (lastScheduleTime != terms.initialExchangeDate || lastEventType != EventType.IED)
        ) {
            eventTypeNextEvent = EventType.IED;
            scheduleTimeNextEvent = terms.initialExchangeDate;
        }

        // maturity event
        if (
            (terms.maturityDate != 0 && (lastScheduleTime <= terms.maturityDate))
            && (scheduleTimeNextEvent == 0 || terms.maturityDate < scheduleTimeNextEvent)
            && (lastScheduleTime != terms.maturityDate || lastEventType != EventType.MD)
        ) {
            eventTypeNextEvent = EventType.MD;
            scheduleTimeNextEvent = terms.maturityDate;
        }

        return encodeEvent(eventTypeNextEvent, scheduleTimeNextEvent);
    }

    /**
     * @notice Computes a schedule segment of cyclic contract events based on the contract terms
     * and the specified timestamps.
     * @param terms terms of the contract
     * @param lastScheduleTime last occurrence of cyclic event
     * @param eventType eventType of the cyclic schedule
     * @return event schedule segment
     */
    function computeNextCyclicEvent(
        CERTFTerms calldata terms,
        uint256 lastScheduleTime,
        EventType eventType
    )
        external
        pure
        override
        returns(bytes32)
    {
        if (eventType == EventType.COF) {
            if (terms.cycleAnchorDateOfCoupon != 0) {
                uint256 nextCouponDate = computeNextCycleDateFromPrecedingDate(
                    terms.cycleOfCoupon,
                    terms.endOfMonthConvention,
                    terms.cycleAnchorDateOfCoupon,
                    lastScheduleTime,
                    true,
                    terms.maturityDate
                );
                if (nextCouponDate == uint256(0)) return bytes32(0);
                return encodeEvent(EventType.COF, nextCouponDate);
            }
        }

         if (eventType == EventType.COP) {
            if (terms.cycleAnchorDateOfCoupon != 0) {
                uint256 nextCouponDate = computeNextCycleDateFromPrecedingDate(
                    terms.cycleOfCoupon,
                    terms.endOfMonthConvention,
                    terms.cycleAnchorDateOfCoupon,
                    lastScheduleTime,
                    true,
                    terms.maturityDate
                );
                if (nextCouponDate == uint256(0)) return bytes32(0);
                uint256 couponPaymentDayScheduleTime = getTimestampPlusPeriod(terms.settlementPeriod, nextCouponDate);
                return encodeEvent(EventType.COF, couponPaymentDayScheduleTime);
            }
        }

        if (eventType == EventType.REF) {
            if (terms.cycleAnchorDateOfRedemption != 0) {
                uint256 nextRedemptionDate = computeNextCycleDateFromPrecedingDate(
                    terms.cycleOfRedemption,
                    terms.endOfMonthConvention,
                    terms.cycleAnchorDateOfRedemption,
                    lastScheduleTime,
                    true,
                    terms.maturityDate
                );
                if (nextRedemptionDate == uint256(0)) return bytes32(0);
                return encodeEvent(EventType.REF, nextRedemptionDate);
            }
        }

        if (eventType == EventType.REP) {
            if (terms.cycleAnchorDateOfRedemption != 0) {
                uint256 nextRedemptionDate = computeNextCycleDateFromPrecedingDate(
                    terms.cycleOfRedemption,
                    terms.endOfMonthConvention,
                    terms.cycleAnchorDateOfRedemption,
                    lastScheduleTime,
                    true,
                    terms.maturityDate
                );
                if (nextRedemptionDate == uint256(0)) return bytes32(0);
                uint256 redemptionPaymentDayScheduleTime = getTimestampPlusPeriod(terms.settlementPeriod, nextRedemptionDate);
                return encodeEvent(EventType.REP, redemptionPaymentDayScheduleTime);
            }
        }

        if (eventType == EventType.EXE) {
            if (terms.cycleAnchorDateOfRedemption != 0) {
                uint256 nextRedemptionDate = computeNextCycleDateFromPrecedingDate(
                    terms.cycleOfRedemption,
                    terms.endOfMonthConvention,
                    terms.cycleAnchorDateOfRedemption,
                    lastScheduleTime,
                    true,
                    terms.maturityDate
                );
                if (nextRedemptionDate == uint256(0)) return bytes32(0);
                if (nextRedemptionDate == terms.maturityDate) return bytes32(0);
                uint256 executionDateScheduleTime = getTimestampPlusPeriod(terms.redemptionExercisePeriod, nextRedemptionDate);
                return encodeEvent(EventType.EXE, executionDateScheduleTime);
            }
        }

       return bytes32(0);
    }

    /**
     * @notice Verifies that the provided event is still scheduled under the terms, the current state of the
     * contract and the current state of the underlying.
     * param _event event for which to check if its still scheduled
     * param terms terms of the contract
     * @param state current state of the contract
     * param hasUnderlying boolean indicating whether the contract has an underlying contract
     * param underlyingState state of the underlying (empty state object if non-existing)
     * @return boolean indicating whether event is still scheduled
     */
    function isEventScheduled(
        bytes32 /* _event */,
        CERTFTerms calldata /* terms */,
        State calldata state,
        bool /* hasUnderlying */,
        State calldata /* underlyingState */
    )
        external
        pure
        override
        returns (bool)
    {
        if (
            state.contractPerformance == ContractPerformance.DF
            || state.contractPerformance == ContractPerformance.MD
            || state.contractPerformance == ContractPerformance.TD
        ) { return false; }

        return true;
    }

    /**
     * @notice Implements abstract method which is defined in BaseEngine.
     * Applies an event to the current state of the contract and returns the resulting state.
     * The inheriting Engine contract has to map the events type to the designated STF.
     * todo Annuity calculator for RR/RRF events, IPCB events and ICB state variable
     * @param terms terms of the contract
     * @param state current state of the contract
     * @param _event event for which to evaluate the next state for
     * @param externalData external data needed for STF evaluation (e.g. rate for RR events)
     * @return the resulting contract state
     */
    function stateTransitionFunction(
        CERTFTerms memory terms,
        State memory state,
        bytes32 _event,
        bytes32 externalData
    )
        internal
        pure
        returns (State memory)
    {
        (EventType eventType, uint256 scheduleTime) = decodeEvent(_event);

        if (eventType == EventType.ISS) return STF_CERTF_ISS(terms, state, scheduleTime, externalData);
        if (eventType == EventType.IED) return STF_CERTF_IED(terms, state, scheduleTime, externalData);
        if (eventType == EventType.COF) return STF_CERTF_COF(terms, state, scheduleTime, externalData);
        if (eventType == EventType.COP) return STF_CERTF_COP(terms, state, scheduleTime, externalData);
        if (eventType == EventType.REF) return STF_CERTF_REF(terms, state, scheduleTime, externalData);
        if (eventType == EventType.EXE) return STF_CERTF_EXE(terms, state, scheduleTime, externalData);
        if (eventType == EventType.REP) return STF_CERTF_REP(terms, state, scheduleTime, externalData);
        if (eventType == EventType.TD) return STF_CERTF_TD(terms, state, scheduleTime, externalData);
        if (eventType == EventType.MD) return STF_CERTF_MD(terms, state, scheduleTime, externalData);
        if (eventType == EventType.CE) return STF_CERTF_CE(terms, state, scheduleTime, externalData);

        revert("CERTFEngine.stateTransitionFunction: ATTRIBUTE_NOT_FOUND");
    }

    /**
     * @notice Implements abstract method which is defined in BaseEngine.
     * Computes the payoff for an event under the current state of the contract.
     * The inheriting Engine contract has to map the events type to the designated POF.
     * @param terms terms of the contract
     * @param state current state of the contract
     * @param _event event for which the payoff should be evaluated
     * @param externalData external data needed for POF evaluation
     * @return the payoff of the event
     */
    function payoffFunction(
        CERTFTerms memory terms,
        State memory state,
        bytes32 _event,
        bytes32 externalData
    )
        internal
        pure
        returns (int256)
    {
        (EventType eventType, uint256 scheduleTime) = decodeEvent(_event);

        if (eventType == EventType.ISS) return 0;
        if (eventType == EventType.COF) return 0;
        if (eventType == EventType.REF) return 0;
        if (eventType == EventType.EXE) return 0;
        if (eventType == EventType.MD) return 0;
        if (eventType == EventType.CE) return 0;
        if (eventType == EventType.IED) return POF_CERTF_IED(terms, state, scheduleTime, externalData);
        if (eventType == EventType.COP) return POF_CERTF_COP(terms, state, scheduleTime, externalData);
        if (eventType == EventType.REP) return POF_CERTF_REP(terms, state, scheduleTime, externalData);
        if (eventType == EventType.TD) return POF_CERTF_TD(terms, state, scheduleTime, externalData);

        revert("CERTFEngine.payoffFunction: ATTRIBUTE_NOT_FOUND");
    }
}

contracts/ACTUS/Engines/CERTF/CERTFSTF.s.content:
// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/math/SignedSafeMath.sol";

import "../../Core/Core.sol";
import "../../Core/SignedMath.sol";


/**
 * @title STF
 * @notice Contains all state transition functions (STFs) for CERTF contracts
 */
contract CERTFSTF is Core {

    using SignedSafeMath for int;
    using SignedMath for int;


    /**
     * State transition for CERTF issue events
     * @param state the old state
     * @return the new state
     */
    function STF_CERTF_ISS (
        CERTFTerms memory terms,
        State memory state,
        uint256 scheduleTime,
        bytes32 /* externalData */
    )
        internal
        pure
        returns (State memory)
    {
        state.quantity = terms.quantity;
        state.statusDate = scheduleTime;

        return state;
    }

    /**
     * State transition for CERTF initial exchange
     * @param state the old state
     * @return the new state
     */
    function STF_CERTF_IED (
        CERTFTerms memory /* terms */,
        State memory state,
        uint256 scheduleTime,
        bytes32 /* externalData */
    )
        internal
        pure
        returns (State memory)
    {
        state.statusDate = scheduleTime;
        return state;
    }

    /**
     * State transition for CERTF coupon fixing day
     * @param state the old state
     * @return the new state
     */
    function STF_CERTF_COF (
        CERTFTerms memory terms,
        State memory state,
        uint256 scheduleTime,
        bytes32 /* externalData */
    )
        internal
        pure
        returns (State memory)
    {
        if (terms.couponType == CouponType.FIX) {
            state.couponAmountFixed = yearFraction(
                shiftCalcTime(state.lastCouponFixingDate, terms.businessDayConvention, terms.calendar, terms.maturityDate),
                shiftCalcTime(scheduleTime, terms.businessDayConvention, terms.calendar, terms.maturityDate),
                terms.dayCountConvention,
                terms.maturityDate
            ).floatMult(terms.nominalPrice).floatMult(terms.couponRate);
        }

        state.lastCouponFixingDate = scheduleTime;
        state.statusDate = scheduleTime;

        return state;
    }


    /**
     * State transition for CERTF coupon payment day
     * @param state the old state
     * @return the new state
     */
    function STF_CERTF_COP (
        CERTFTerms memory /* terms */,
        State memory state,
        uint256 scheduleTime,
        bytes32 /* externalData */
    )
        internal
        pure
        returns (State memory)
    {
        state.couponAmountFixed = 0;
        state.statusDate = scheduleTime;
        return state;
    }


    /**
     * State transition for CERTF redemption fixing day
     * @param state the old state
     * @return the new state
     */
    function STF_CERTF_REF (
        CERTFTerms memory terms,
        State memory state,
        uint256 scheduleTime,
        bytes32 externalData
    )
        internal
        pure
        returns (State memory)
    {
        state.exerciseAmount = int256(externalData)
        .floatMult(terms.nominalPrice)
        .floatMult(state.marginFactor)
        .floatMult(state.adjustmentFactor);

        state.statusDate = scheduleTime;

        return state;
    }

    /**
     * State transition for CERTF exercise day
     * @param state the old state
     * @return the new state
     */
    function STF_CERTF_EXE (
        CERTFTerms memory /* terms */,
        State memory state,
        uint256 scheduleTime,
        bytes32 externalData
    )
        internal
        pure
        returns (State memory)
    {
        state.exerciseQuantity = int256(externalData);
        state.statusDate = scheduleTime;

        return state;
    }

    /**
     * State transition for CERTF Redemption Payment Day
     * @param state the old state
     * @return the new state
     */
    function STF_CERTF_REP (
        CERTFTerms memory /* terms */,
        State memory state,
        uint256 scheduleTime,
        bytes32 /* externalData */
    )
        internal
        pure
        returns (State memory)
    {
        state.quantity = state.quantity.sub(state.exerciseQuantity);
        state.exerciseQuantity = 0;
        state.exerciseAmount = 0;
        state.statusDate = scheduleTime;

        if (scheduleTime == state.maturityDate) {
            state.contractPerformance = ContractPerformance.MD;
        } else if (scheduleTime == state.terminationDate) {
            state.contractPerformance = ContractPerformance.TD;
        }

        return state;
    }

    /**
     * State transition for CERTF termination events
     * @param state the old state
     * @return the new state
     */
    function STF_CERTF_TD (
        CERTFTerms memory /* terms */,
        State memory state,
        uint256 scheduleTime,
        bytes32 /* externalData */
    )
        internal
        pure
        returns (State memory)
    {
        state.quantity = 0;
        state.terminationDate = scheduleTime;
        state.statusDate = scheduleTime;

        return state;
    }

    /**
     * State transition for CERTF maturity
     * @param state the old state
     * @return the new state
     */
    function STF_CERTF_MD (
        CERTFTerms memory /* terms */,
        State memory state,
        uint256 scheduleTime,
        bytes32 /* externalData */
    )
        internal
        pure
        returns (State memory)
    {
        state.maturityDate = scheduleTime;
        state.statusDate = scheduleTime;

        return state;
    }

    /**
     * State transition for CERTF settlement
     * @param state the old state
     * @return the new state
     */
    function STF_CERTF_CE (
        CERTFTerms memory terms,
        State memory state,
        uint256 scheduleTime,
        bytes32 externalData
    )
        internal
        pure
        returns (State memory)
    {
        // handle maturity date
        uint256 nonPerformingDate = (state.nonPerformingDate == 0)
            ? shiftEventTime(
                scheduleTime,
                terms.businessDayConvention,
                terms.calendar,
                terms.maturityDate
            ) : state.nonPerformingDate;

        uint256 currentTimestamp = uint256(externalData);

        bool isInGracePeriod = false;
        if (terms.gracePeriod.isSet) {
            uint256 graceDate = getTimestampPlusPeriod(terms.gracePeriod, nonPerformingDate);
            if (currentTimestamp <= graceDate) {
                state.contractPerformance = ContractPerformance.DL;
                isInGracePeriod = true;
            }
        }

        if (terms.delinquencyPeriod.isSet && !isInGracePeriod) {
            uint256 delinquencyDate = getTimestampPlusPeriod(terms.delinquencyPeriod, nonPerformingDate);
            if (currentTimestamp <= delinquencyDate) {
                state.contractPerformance = ContractPerformance.DQ;
            } else {
                state.contractPerformance = ContractPerformance.DF;
            }
        }

        if (state.nonPerformingDate == 0) {
            // handle maturity date
            state.nonPerformingDate = shiftEventTime(
                scheduleTime,
                terms.businessDayConvention,
                terms.calendar,
                terms.maturityDate
            );
        }

        return state;
    }
}

contracts/ACTUS/Engines/CERTF/CERTFPOF.s.content:
// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/math/SignedSafeMath.sol";

import "../../Core/Core.sol";
import "../../Core/SignedMath.sol";


/**
 * @title POF
 * @notice Contains all Payoff Functions (POFs) for CERTF contracts
 */
contract CERTFPOF is Core {

    using SignedSafeMath for int;
    using SignedMath for int;


    /**
     * Payoff Function for CERTF initial exchange
     * @return the new state
     */
    function POF_CERTF_IED (
        CERTFTerms memory terms,
        State memory state,
        uint256 /* scheduleTime */,
        bytes32 /* externalData */
    )
        internal
        pure
        returns(int256)
    {
        return (
            roleSign(terms.contractRole) * state.quantity.floatMult(terms.issuePrice)
        );
    }

    /**
     * Payoff Function for CERTF coupon payment day
     * @return the new state
     */
    function POF_CERTF_COP (
        CERTFTerms memory terms,
        State memory state,
        uint256 /* scheduleTime */,
        bytes32 /* externalData */
    )
        internal
        pure
        returns(int256)
    {
        return (
            roleSign(terms.contractRole) * state.quantity.floatMult(state.couponAmountFixed)
        );
    }

    /**
     * Payoff Function for CERTF Redemption Payment Day
     * @return the new state
     */
    function POF_CERTF_REP (
        CERTFTerms memory terms,
        State memory state,
        uint256 /* scheduleTime */,
        bytes32 /* externalData */
    )
        internal
        pure
        returns(int256)
    {
        return (
            roleSign(terms.contractRole) * state.exerciseQuantity.floatMult(state.exerciseAmount)
        );
    }

     /**
     * Payoff Function for CERTF termination events
     * @return the new state
     */
    function POF_CERTF_TD (
        CERTFTerms memory terms,
        State memory state,
        uint256 /* scheduleTime */,
        bytes32 /* externalData */
    )
        internal
        pure
        returns(int256)
    {
        return (
            roleSign(terms.contractRole) * state.quantity.floatMult(state.exerciseAmount)
        );
    }
}

contracts/Core/CEC/CECRegistry.sol.content:
// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "../../ACTUS/Engines/CEC/ICECEngine.sol";

import "../Base/SharedTypes.sol";
import "../Base/AssetRegistry/BaseRegistry.sol";
import "./CECEncoder.sol";
import "./ICECRegistry.sol";


/**
 * @title CECRegistry
 * @notice Registry for ACTUS Protocol assets
 */
contract CECRegistry is BaseRegistry, ICECRegistry {

    using CECEncoder for Asset;


    constructor() BaseRegistry() {}

    /**
     * @notice
     * @param assetId id of the asset
     * @param terms asset specific terms (CECTerms)
     * @param state initial state of the asset
     * @param schedule schedule of the asset
     * @param ownership ownership of the asset
     * @param engine ACTUS Engine of the asset
     * @param actor account which is allowed to update the asset state
     * @param admin account which as admin rights (optional)
     */
    function registerAsset(
        bytes32 assetId,
        CECTerms calldata terms,
        State calldata state,
        bytes32[] calldata schedule,
        AssetOwnership calldata ownership,
        address engine,
        address actor,
        address admin
    )
        external
        override
        onlyApprovedActors
    {
        setAsset(assetId, state, schedule, ownership, engine, actor, admin);
        assets[assetId].encodeAndSetCECTerms(terms);
    }

    /**
     * @notice Returns the terms of an asset.
     * @param assetId id of the asset
     * @return terms of the asset
     */
    function getTerms(bytes32 assetId)
        external
        view
        override
        returns (CECTerms memory)
    {
        return assets[assetId].decodeAndGetCECTerms();
    }

    /**
     * @notice Set the terms of the asset
     * @dev Can only be set by authorized account.
     * @param assetId id of the asset
     * @param terms new terms
     */
    function setTerms(bytes32 assetId, CECTerms calldata terms)
        external
        override
        isAuthorized (assetId)
    {
        assets[assetId].encodeAndSetCECTerms(terms);
        emit UpdatedTerms(assetId);
    }

    function getEnumValueForTermsAttribute(bytes32 assetId, bytes32 attribute)
        public
        view
        override(ITermsRegistry, TermsRegistry)
        returns (uint8)
    {
        return assets[assetId].decodeAndGetEnumValueForCECAttribute(attribute);
    }

    function getAddressValueForTermsAttribute(bytes32 assetId, bytes32 attribute)
        public
        view
        override(ITermsRegistry, TermsRegistry)
        returns (address)
    {
        return assets[assetId].decodeAndGetAddressValueForForCECAttribute(attribute);
    }

    function getBytes32ValueForTermsAttribute(bytes32 assetId, bytes32 attribute)
        public
        view
        override(ITermsRegistry, TermsRegistry)
        returns (bytes32)
    {
        return assets[assetId].decodeAndGetBytes32ValueForForCECAttribute(attribute);
    }

    function getUIntValueForTermsAttribute(bytes32 assetId, bytes32 attribute)
        public
        view
        override(ITermsRegistry, TermsRegistry)
        returns (uint256)
    {
        return assets[assetId].decodeAndGetUIntValueForForCECAttribute(attribute);
    }

    function getIntValueForTermsAttribute(bytes32 assetId, bytes32 attribute)
        public
        view
        override(ITermsRegistry, TermsRegistry)
        returns (int256)
    {
        return assets[assetId].decodeAndGetIntValueForForCECAttribute(attribute);
    }

    function getPeriodValueForTermsAttribute(bytes32 assetId, bytes32 attribute)
        public
        view
        override(ITermsRegistry, TermsRegistry)
        returns (IP memory)
    {
        return assets[assetId].decodeAndGetPeriodValueForForCECAttribute(attribute);
    }

    function getCycleValueForTermsAttribute(bytes32 assetId, bytes32 attribute)
        public
        view
        override(ITermsRegistry, TermsRegistry)
        returns (IPS memory)
    {
        return assets[assetId].decodeAndGetCycleValueForForCECAttribute(attribute);
    }

    function getContractReferenceValueForTermsAttribute(bytes32 assetId, bytes32 attribute)
        public
        view
        override(ITermsRegistry, TermsRegistry)
        returns (ContractReference memory)
    {
        return assets[assetId].decodeAndGetContractReferenceValueForCECAttribute(attribute);
    }

    function getNextComputedEvent(bytes32 assetId)
        internal
        view
        override(TermsRegistry)
        returns (bytes32, bool)
    {
        Asset storage asset = assets[assetId];
        CECTerms memory terms = asset.decodeAndGetCECTerms();

        EventType nextEventType;
        uint256 nextScheduleTime;
        bool isCyclicEvent = true;

        // Non-Cyclic
        {
            (EventType eventType, uint256 scheduleTime) = decodeEvent(ICECEngine(asset.engine).computeNextNonCyclicEvent(
                terms,
                asset.schedule.lastNonCyclicEvent
            ));

            if (
                (nextScheduleTime == 0)
                || (scheduleTime != 0 && scheduleTime < nextScheduleTime)
                || (scheduleTime != 0 && nextScheduleTime == scheduleTime && getEpochOffset(eventType) < getEpochOffset(nextEventType))
            ) {
                nextScheduleTime = scheduleTime;
                nextEventType = eventType;
                isCyclicEvent = false;
            }        
        }

        return (encodeEvent(nextEventType, nextScheduleTime), isCyclicEvent);
    }
}

contracts/Core/CEC/CECEncoder.sol.content:
// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "../Base/SharedTypes.sol";
import "../Base/AssetRegistry/BaseRegistryStorage.sol";


library CECEncoder {

    function storeInPackedTerms(Asset storage asset, bytes32 attributeKey, bytes32 value) private {
        // skip if value did not change
        if (asset.packedTerms[attributeKey] == value) return;
        asset.packedTerms[attributeKey] = value;
    }

    /**
     * @dev Tightly pack and store only non-zero overwritten terms (LifecycleTerms)
     * @notice All non zero values of the overwrittenTerms object are stored.
     * It does not check if overwrittenAttributesMap actually marks attribute as overwritten.
     */
    function encodeAndSetCECTerms(Asset storage asset, CECTerms memory terms) external {
        storeInPackedTerms(
            asset,
            "enums",
            bytes32(uint256(uint8(terms.contractType))) << 248 |
            bytes32(uint256(uint8(terms.calendar))) << 240 |
            bytes32(uint256(uint8(terms.contractRole))) << 232 |
            bytes32(uint256(uint8(terms.dayCountConvention))) << 224 |
            bytes32(uint256(uint8(terms.businessDayConvention))) << 216 |
            bytes32(uint256(uint8(terms.endOfMonthConvention))) << 208 |
            bytes32(uint256(uint8(terms.creditEventTypeCovered))) << 200 |
            bytes32(uint256(uint8(terms.feeBasis))) << 192
        );

        storeInPackedTerms(asset, "statusDate", bytes32(terms.statusDate));
        storeInPackedTerms(asset, "maturityDate", bytes32(terms.maturityDate));

        storeInPackedTerms(asset, "notionalPrincipal", bytes32(terms.notionalPrincipal));

        storeInPackedTerms(asset, "feeRate", bytes32(terms.feeRate));
        storeInPackedTerms(asset, "coverageOfCreditEnhancement", bytes32(terms.coverageOfCreditEnhancement));

        storeInPackedTerms(
            asset,
            "contractReference_1_type_role",
            bytes32(uint256(terms.contractReference_1._type)) << 16 |
            bytes32(uint256(terms.contractReference_1.role)) << 8
        );

        storeInPackedTerms(
            asset,
            "contractReference_1_object",
            terms.contractReference_1.object
        );
        storeInPackedTerms(
            asset,
            "contractReference_1_object2",
            terms.contractReference_1.object2
        );

        storeInPackedTerms(
            asset,
            "contractReference_2_type_role",
            bytes32(uint256(terms.contractReference_2._type)) << 16 |
            bytes32(uint256(terms.contractReference_2.role)) << 8
        );

        storeInPackedTerms(
            asset,
            "contractReference_2_object",
            terms.contractReference_2.object
        );
        storeInPackedTerms(
            asset,
            "contractReference_2_object2",
            terms.contractReference_2.object2
        );
    }

    /**
     * @dev Decode and loads CECTerms
     */
    function decodeAndGetCECTerms(Asset storage asset) external view returns (CECTerms memory) {
        return CECTerms(
            ContractType(uint8(uint256(asset.packedTerms["enums"] >> 248))),
            Calendar(uint8(uint256(asset.packedTerms["enums"] >> 240))),
            ContractRole(uint8(uint256(asset.packedTerms["enums"] >> 232))),
            DayCountConvention(uint8(uint256(asset.packedTerms["enums"] >> 224))),
            BusinessDayConvention(uint8(uint256(asset.packedTerms["enums"] >> 216))),
            EndOfMonthConvention(uint8(uint256(asset.packedTerms["enums"] >> 208))),
            ContractPerformance(uint8(uint256(asset.packedTerms["enums"] >> 200))),
            FeeBasis(uint8(uint256(asset.packedTerms["enums"] >> 192))),

            uint256(asset.packedTerms["statusDate"]),
            uint256(asset.packedTerms["maturityDate"]),

            int256(asset.packedTerms["notionalPrincipal"]),
            int256(asset.packedTerms["feeRate"]),
            int256(asset.packedTerms["coverageOfCreditEnhancement"]),

            ContractReference(
                asset.packedTerms["contractReference_1_object"],
                asset.packedTerms["contractReference_1_object2"],
                ContractReferenceType(uint8(uint256(asset.packedTerms["contractReference_1_type_role"] >> 16))),
                ContractReferenceRole(uint8(uint256(asset.packedTerms["contractReference_1_type_role"] >> 8)))
            ),
            ContractReference(
                asset.packedTerms["contractReference_2_object"],
                asset.packedTerms["contractReference_2_object2"],
                ContractReferenceType(uint8(uint256(asset.packedTerms["contractReference_2_type_role"] >> 16))),
                ContractReferenceRole(uint8(uint256(asset.packedTerms["contractReference_2_type_role"] >> 8)))
            )
        );
    }

    function decodeAndGetEnumValueForCECAttribute(Asset storage asset, bytes32 attributeKey)
        external
        view
        returns (uint8)
    {
        if (attributeKey == "contractType") {
            return uint8(uint256(asset.packedTerms["enums"] >> 248));
        } else if (attributeKey == bytes32("calendar")) {
            return uint8(uint256(asset.packedTerms["enums"] >> 240));
        } else if (attributeKey == bytes32("contractRole")) {
            return uint8(uint256(asset.packedTerms["enums"] >> 232));
        } else if (attributeKey == bytes32("dayCountConvention")) {
            return uint8(uint256(asset.packedTerms["enums"] >> 224));
        } else if (attributeKey == bytes32("businessDayConvention")) {
            return uint8(uint256(asset.packedTerms["enums"] >> 216));
        } else if (attributeKey == bytes32("endOfMonthConvention")) {
            return uint8(uint256(asset.packedTerms["enums"] >> 208));
        } else if (attributeKey == bytes32("creditEventTypeCovered")) {
            return uint8(uint256(asset.packedTerms["enums"] >> 200));
        } else if (attributeKey == bytes32("feeBasis")) {
            return uint8(uint256(asset.packedTerms["enums"] >> 192));
        } else {
            return uint8(0);
        }
    }

    function decodeAndGetAddressValueForForCECAttribute(Asset storage /* asset */, bytes32 /* attributeKey */)
        external
        pure
        returns (address)
    {
        return address(0);
    }

    function decodeAndGetBytes32ValueForForCECAttribute(Asset storage asset, bytes32 attributeKey)
        external
        view
        returns (bytes32)
    {
        return asset.packedTerms[attributeKey];
    }

    function decodeAndGetUIntValueForForCECAttribute(Asset storage asset, bytes32 attributeKey)
        external
        view
        returns (uint256)
    {
        return uint256(asset.packedTerms[attributeKey]);
    }

    function decodeAndGetIntValueForForCECAttribute(Asset storage asset, bytes32 attributeKey)
        external
        view
        returns (int256)
    {
        return int256(asset.packedTerms[attributeKey]);
    }

    function decodeAndGetPeriodValueForForCECAttribute(Asset storage /* asset */, bytes32 /* attributeKey */)
        external
        pure
        returns (IP memory)
    {
        return IP(0, P(0), false);
    }

    function decodeAndGetCycleValueForForCECAttribute(Asset storage /* asset */, bytes32 /* attributeKey */)
        external
        pure
        returns (IPS memory)
    {
        return IPS(0, P(0), S(0), false);
    }

    function decodeAndGetContractReferenceValueForCECAttribute(Asset storage asset , bytes32 attributeKey )
        external
        view
        returns (ContractReference memory)
    {
        if (attributeKey == bytes32("contractReference_1")) {
            return ContractReference(
                asset.packedTerms["contractReference_1_object"],
                asset.packedTerms["contractReference_1_object2"],
                ContractReferenceType(uint8(uint256(asset.packedTerms["contractReference_1_type_role"] >> 16))),
                ContractReferenceRole(uint8(uint256(asset.packedTerms["contractReference_1_type_role"] >> 8)))
            );
        } else if (attributeKey == bytes32("contractReference_2")) {
            return ContractReference(
                asset.packedTerms["contractReference_2_object"],
                asset.packedTerms["contractReference_2_object2"],
                ContractReferenceType(uint8(uint256(asset.packedTerms["contractReference_2_type_role"] >> 16))),
                ContractReferenceRole(uint8(uint256(asset.packedTerms["contractReference_2_type_role"] >> 8)))
            );
        } else {
            return ContractReference(
                bytes32(0),
                bytes32(0),
                ContractReferenceType(0),
                ContractReferenceRole(0)
            );
        }
    }
}

contracts/Core/CEC/ICECRegistry.sol.content:
// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "../Base/SharedTypes.sol";
import "../Base/AssetRegistry/IAssetRegistry.sol";


interface ICECRegistry is IAssetRegistry {

    function registerAsset(
        bytes32 assetId,
        CECTerms calldata terms,
        State calldata state,
        bytes32[] calldata schedule,
        AssetOwnership calldata ownership,
        address engine,
        address actor,
        address admin
    )
        external;

    function getTerms(bytes32 assetId)
        external
        view
        returns (CECTerms memory);

    function setTerms(bytes32 assetId, CECTerms calldata terms)
        external;
}

contracts/Core/CEC/CECActor.sol.content:
// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "../../ACTUS/Engines/CEC/ICECEngine.sol";

import "../Base/AssetActor/BaseActor.sol";
import "../Base/Custodian/ICustodian.sol";
import "./ICECRegistry.sol";


/**
 * @title CECActor
 * @notice TODO
 */
contract CECActor is BaseActor {

    using SignedMath for int;


    constructor(IAssetRegistry assetRegistry, IOracleRegistry oracleRegistry) BaseActor(assetRegistry, oracleRegistry) {}

    /**
     * @notice Derives initial state of the asset terms and stores together with
     * terms, schedule, ownership, engine, admin of the asset in the contract types specific AssetRegistry.
     * @param terms asset specific terms
     * @param schedule schedule of the asset
     * @param engine address of the ACTUS engine used for the spec. ContractType
     * @param admin address of the admin of the asset (optional)
     * @param custodian address of the custodian of the collateral
     * @param underlyingRegistry address of the asset registry where the underlying asset is stored
     */
    function initialize(
        CECTerms calldata terms,
        bytes32[] calldata schedule,
        address engine,
        address admin,
        address custodian,
        address underlyingRegistry
    )
        external
    {
        require(
            engine != address(0) && IEngine(engine).contractType() == ContractType.CEC,
            "ANNActor.initialize: CONTRACT_TYPE_OF_ENGINE_UNSUPPORTED"
        );

        // solium-disable-next-line
        bytes32 assetId = keccak256(abi.encode(terms, block.timestamp));
        AssetOwnership memory ownership;

        // check if first contract reference in terms references an underlying asset
        if (terms.contractReference_1.role == ContractReferenceRole.COVE) {
            require(
                terms.contractReference_1.object != bytes32(0),
                "CECActor.initialize: INVALID_CONTRACT_REFERENCE_1_OBJECT"
            );
        }

        // check if second contract reference in terms contains a reference to collateral
        if (terms.contractReference_2.role == ContractReferenceRole.COVI) {
            require(
                terms.contractReference_2.object != bytes32(0),
                "CECActor.initialize: INVALID_CONTRACT_REFERENCE_2_OBJECT"
            );

            // derive assetId
            // solium-disable-next-line
            assetId = keccak256(abi.encode(terms, address(custodian), block.timestamp));

            // derive underlying assetId
            bytes32 underlyingAssetId = terms.contractReference_1.object;
            // get contract role and ownership of referenced underlying asset
            ContractRole underlyingContractRole = ContractRole(assetRegistry.getEnumValueForTermsAttribute(underlyingAssetId, "contractRole"));
            AssetOwnership memory underlyingAssetOwnership = IAssetRegistry(underlyingRegistry).getOwnership(underlyingAssetId);

            // set ownership of draft according to contract role of underlying
            if (terms.contractRole == ContractRole.BUY && underlyingContractRole == ContractRole.RPA) {
                ownership = AssetOwnership(
                    underlyingAssetOwnership.creatorObligor,
                    underlyingAssetOwnership.creatorBeneficiary,
                    address(custodian),
                    underlyingAssetOwnership.counterpartyBeneficiary
                );
            } else if (terms.contractRole == ContractRole.SEL && underlyingContractRole == ContractRole.RPL) {
                ownership = AssetOwnership(
                    address(custodian),
                    underlyingAssetOwnership.creatorBeneficiary,
                    underlyingAssetOwnership.counterpartyObligor,
                    underlyingAssetOwnership.counterpartyBeneficiary
                );
            } else {
                // only BUY, RPA and SEL, RPL allowed for CEC
                revert("CECActor.initialize: INVALID_CONTRACT_ROLES");
            }

            // execute contractual conditions
            // try transferring collateral to the custodian
            ICustodian(custodian).lockCollateral(assetId, terms, ownership);
        }

        // compute the initial state of the asset
        State memory initialState = ICECEngine(engine).computeInitialState(terms);

        // register the asset in the AssetRegistry
        ICECRegistry(address(assetRegistry)).registerAsset(
            assetId,
            terms,
            initialState,
            schedule,
            ownership,
            engine,
            address(this),
            admin
        );

        emit InitializedAsset(assetId, ContractType.CEC, ownership.creatorObligor, ownership.counterpartyObligor);
    }

    function computeStateAndPayoffForEvent(bytes32 assetId, State memory state, bytes32 _event)
        internal
        view
        override
        returns (State memory, int256)
    {
        address engine = assetRegistry.getEngine(assetId);
        CECTerms memory terms = ICECRegistry(address(assetRegistry)).getTerms(assetId);
        (EventType eventType, uint256 scheduleTime) = decodeEvent(_event);

        int256 payoff = ICECEngine(engine).computePayoffForEvent(
            terms,
            state,
            _event,
            getExternalDataForPOF(
                assetId,
                eventType,
                shiftCalcTime(scheduleTime, terms.businessDayConvention, terms.calendar, terms.maturityDate)
            )
        );
        state = ICECEngine(engine).computeStateForEvent(
            terms,
            state,
            _event,
            getExternalDataForSTF(
                assetId,
                eventType,
                shiftCalcTime(scheduleTime, terms.businessDayConvention, terms.calendar, terms.maturityDate)
            )
        );

        return (state, payoff);
    }

    /**
     * @notice Retrieves external data (such as market object data, block time, underlying asset state)
     * used for evaluating the STF for a given event.
     */
    function getExternalDataForSTF(
        bytes32 assetId,
        EventType eventType,
        uint256 /* timestamp */
    )
        internal
        view
        override
        returns (bytes32)
    {
        if (eventType == EventType.EXE) {
            // get the remaining notionalPrincipal from the underlying
            ContractReference memory contractReference_1 = assetRegistry.getContractReferenceValueForTermsAttribute(
                assetId,
                "contractReference_1"
            );
            if (contractReference_1.role == ContractReferenceRole.COVE) {
                bytes32 underlyingAssetId = contractReference_1.object;
                address underlyingRegistry = address(uint160(uint256(contractReference_1.object2)));
                require(
                    IAssetRegistry(underlyingRegistry).isRegistered(underlyingAssetId) == true,
                    "BaseActor.getExternalDataForSTF: ASSET_DOES_NOT_EXIST"
                );
                return bytes32(
                    IAssetRegistry(underlyingRegistry).getIntValueForStateAttribute(underlyingAssetId, "notionalPrincipal")
                );
            }
        }

        return bytes32(0);
    }
}

