contracts/Core/Base/Custodian/Custodian..content:
// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";

import "../Conversions.sol";
import "../../CEC/ICECRegistry.sol";
import "./ICustodian.sol";


/**
 * @title Custodian
 * @notice Contract which holds the collateral of CEC (Credit Enhancement Collateral) assets.
 */
contract Custodian is ICustodian, ReentrancyGuard, Conversions {

    using SafeMath for uint256;

    event LockedCollateral(bytes32 indexed assetId, address collateralizer, uint256 collateralAmount);
    event ReturnedCollateral(bytes32 indexed assetId, address collateralizer, uint256 returnedAmount);

    address public cecActor;
    ICECRegistry public cecRegistry;
    mapping(bytes32 => bool) internal collateral;


    constructor(address _cecActor, ICECRegistry _cecRegistry) {
        cecActor = _cecActor;
        cecRegistry = _cecRegistry;
    }

    /**
     * @notice Locks the required collateral amount encoded in the second contract
     * reference in the terms.
     * @dev The collateralizer has to set allowance beforehand. The custodian increases
     * allowance for the AssetActor by amount of collateral
     * @param assetId id of the asset with collateral requirements
     * @param terms terms of the asset containing the collateral requirements
     * @param ownership ownership of the asset
     * @return true if the collateral was locked by the Custodian
     */
    function lockCollateral(
        bytes32 assetId,
        CECTerms calldata terms,
        AssetOwnership calldata ownership
    )
        external
        override
        returns (bool)
    {
        require(
            terms.contractRole == ContractRole.BUY || terms.contractRole == ContractRole.SEL,
            "Custodian.lockCollateral: INVALID_CONTRACT_ROLE"
        );

        require(
            (terms.contractRole == ContractRole.BUY)
                ? ownership.counterpartyObligor == address(this)
                : ownership.creatorObligor == address(this),
            "Custodian.lockCollateral: INVALID_OWNERSHIP"
        );

        // derive address of collateralizer
        address collateralizer = (terms.contractRole == ContractRole.BUY)
            ? ownership.counterpartyBeneficiary
            : ownership.creatorBeneficiary;

        // decode token address and amount of collateral
        (address collateralToken, uint256 collateralAmount) = decodeCollateralObject(terms.contractReference_2.object);

        require(
            IERC20(collateralToken).allowance(collateralizer, address(this)) >= collateralAmount,
            "Custodian.lockCollateral: INSUFFICIENT_ALLOWANCE"
        );

        // try transferring collateral from collateralizer to the custodian
        require(
            IERC20(collateralToken).transferFrom(collateralizer, address(this), collateralAmount),
            "Custodian.lockCollateral: TRANSFER_FAILED"
        );

        // set allowance for AssetActor to later transfer collateral when EXE is triggered
        uint256 allowance = IERC20(collateralToken).allowance(address(this), cecActor);
        require(
            IERC20(collateralToken).approve(cecActor, allowance.add(collateralAmount)),
            "Custodian.lockCollateral: INCREASING_ALLOWANCE_FAILED"
        );

        // register collateral for assetId
        collateral[assetId] = true;

        emit LockedCollateral(assetId, collateralizer, collateralAmount);

        return true;
    }

    /**
     * @notice Returns the entire collateral back to the collateralizer if collateral
     * was not executed before the asset reached maturity or it returns the remaining
     * collateral (not executed amount) after collateral was executed and settled
     * @dev resets allowance for the Asset Actor,
     * reverts if state of the asset does not allow unlocking the collateral
     * @param assetId id of the asset for which to return the collateral,
     * @return true if the collateral was returned to the collateralizer
     */
    function returnCollateral(
        bytes32 assetId
    )
        external
        override
        returns (bool)
    {
        require(
            collateral[assetId] == true,
            "Custodian.returnCollateral: ENTRY_DOES_NOT_EXIST"
        );

        ContractRole contractRole = ContractRole(cecRegistry.getEnumValueForTermsAttribute(assetId, "contractRole"));
        ContractReference memory contractReference_2 = cecRegistry.getContractReferenceValueForTermsAttribute(assetId, "contractReference_2");
        State memory state = cecRegistry.getState(assetId);
        AssetOwnership memory ownership = cecRegistry.getOwnership(assetId);

        // derive address of collateralizer
        address collateralizer = (contractRole == ContractRole.BUY)
            ? ownership.counterpartyBeneficiary
            : ownership.creatorBeneficiary;

        // decode token address and amount of collateral
        (address collateralToken, uint256 collateralAmount) = decodeCollateralObject(contractReference_2.object);

        // calculate amount to return
        uint256 notExecutedAmount;
        // if EXE was triggerd
        if (state.exerciseDate != uint256(0)) {
            notExecutedAmount = collateralAmount.sub(
                (state.exerciseAmount >= 0) ? uint256(state.exerciseAmount) : uint256(-1 * state.exerciseAmount)
            );
        // if EXE was not triggered and (reached maturity or was terminated)
        } else if (
            state.exerciseDate == uint256(0)
            && (state.contractPerformance == ContractPerformance.MD || state.contractPerformance == ContractPerformance.TD)
        ) {
            notExecutedAmount = collateralAmount;
        // throw if EXE was not triggered and maturity is not reached
        } else {
            revert("Custodian.returnCollateral: COLLATERAL_CAN_NOT_BE_RETURNED");
        }

        // reset allowance for AssetActor
        uint256 allowance = IERC20(collateralToken).allowance(address(this), cecActor);
        require(
            IERC20(collateralToken).approve(cecActor, allowance.sub(notExecutedAmount)),
            "Custodian.returnCollateral: DECREASING_ALLOWANCE_FAILD"
        );

        // try transferring amount back to the collateralizer
        require(
            IERC20(collateralToken).transfer(collateralizer, notExecutedAmount),
            "Custodian.returnCollateral: TRANSFER_FAILED"
        );

        emit ReturnedCollateral(assetId, collateralizer, notExecutedAmount);

        return true;
    }
}

contracts/ACTUS/Engines/CEC/CECEngine.so.content:
// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/math/SignedSafeMath.sol";

import "../../Core/Core.sol";
import "../../Core/SignedMath.sol";
import "./ICECEngine.sol";
import "./CECSTF.sol";
import "./CECPOF.sol";


/**
 * @title CECEngine
 * @notice Inherits from BaseEngine by implementing STFs, POFs according to the ACTUS standard for a CEC contract
 * @dev All numbers except unix timestamp are represented as multiple of 10 ** 18
 * inputs have to be multiplied by 10 ** 18, outputs have to multplied by 10 ** -18
 */
contract CECEngine is Core, CECSTF, CECPOF, ICECEngine {

    using SignedSafeMath for int;
    using SignedMath for int;


    function contractType() external pure override returns (ContractType) {
        return ContractType.CEC;
    }

    /**
     * Applys an event to the current state of a contract and returns the resulting contract state.
     * @param terms terms of the contract
     * @param state current state of the contract
     * @param _event event to be applied to the contract state
     * @param externalData external data needed for STF evaluation (e.g. rate for RR events)
     * @return the resulting contract state
     */
    function computeStateForEvent(
        CECTerms calldata terms,
        State calldata state,
        bytes32 _event,
        bytes32 externalData
    )
        external
        pure
        override
        returns (State memory)
    {
        return stateTransitionFunction(
            terms,
            state,
            _event,
            externalData
        );
    }

    /**
     * Evaluates the payoff for an event under the current state of the contract.
     * @param terms terms of the contract
     * @param state current state of the contract
     * @param _event event for which the payoff should be evaluated
     * @param externalData external data needed for POF evaluation (e.g. fxRate)
     * @return the payoff of the event
     */
    function computePayoffForEvent(
        CECTerms calldata terms,
        State calldata state,
        bytes32 _event,
        bytes32 externalData
    )
        external
        pure
        override
        returns (int256)
    {
        // // if alternative settlementCurrency is set then apply fxRate to payoff
        // if (terms.settlementCurrency != address(0) && terms.currency != terms.settlementCurrency) {
        //     return payoffFunction(
        //         terms,
        //         state,
        //         _event,
        //         externalData
        //     ).floatMult(int256(externalData));
        // }

        return payoffFunction(
            terms,
            state,
            _event,
            externalData
        );
    }

    /**
     * @notice Initialize contract state space based on the contract terms.
     * @param terms terms of the contract
     * @return initial state of the contract
     */
    function computeInitialState(CECTerms calldata terms)
        external
        pure
        override
        returns (State memory)
    {
        State memory state;

        state.contractPerformance = ContractPerformance.PF;
        state.statusDate = terms.statusDate;
        state.maturityDate = terms.maturityDate;
        state.notionalPrincipal = roleSign(terms.contractRole) * terms.notionalPrincipal;

        return state;
    }

    /**
     * @notice Computes a schedule segment of non-cyclic contract events based on the contract terms
     * and the specified timestamps.
     * @param terms terms of the contract
     * @param segmentStart start timestamp of the segment
     * @param segmentEnd end timestamp of the segement
     * @return segment of the non-cyclic schedule
     */
    function computeNonCyclicScheduleSegment(
        CECTerms calldata terms,
        uint256 segmentStart,
        uint256 segmentEnd
    )
        external
        pure
        override
        returns (bytes32[] memory)
    {
        bytes32[MAX_EVENT_SCHEDULE_SIZE] memory events;
        uint16 index;

        // maturity event
        if (isInSegment(terms.maturityDate, segmentStart, segmentEnd) == true) {
            events[index] = encodeEvent(EventType.MD, terms.maturityDate);
            index++;
        }

        // remove null entries from returned array
        bytes32[] memory schedule = new bytes32[](index);
        for (uint256 i = 0; i < index; i++) {
            schedule[i] = events[i];
        }

        return schedule;
    }

    /**
     * @notice Computes a schedule segment of cyclic contract events based on the contract terms
     * and the specified timestamps.
     * param terms terms of the contract
     * param segmentStart start timestamp of the segment
     * param segmentEnd end timestamp of the segement
     * param eventType eventType of the cyclic schedule
     * @return event schedule segment
     */
    function computeCyclicScheduleSegment(
        CECTerms calldata /* terms */,
        uint256 /* segmentStart */,
        uint256 /* segmentEnd */,
        EventType /* eventType */
    )
        external
        pure
        override
        returns (bytes32[] memory)
    {
        bytes32[] memory schedule = new bytes32[](0);

        return schedule;
    }

    /**
     * @notice Computes the next non-cyclic contract events based on the contract terms
     * and the timestamp on which the prev. event occured.
     * @dev Assumes that non-cyclic events of the same event type have a unique schedule time
     * @param terms terms of the contract
     * @param lastNonCyclicEvent last non-cyclic event
     * @return next non-cyclic event
     */
    function computeNextNonCyclicEvent(
        CECTerms calldata terms,
        bytes32 lastNonCyclicEvent
    )
        external
        pure
        override
        returns (bytes32)
    {
        (EventType lastEventType, uint256 lastScheduleTime) = decodeEvent(lastNonCyclicEvent);

        EventType eventTypeNextEvent;
        uint256 scheduleTimeNextEvent;

        // EventTypes ordered after epoch offset - so we don't have make an additional epochOffset check

        // maturity event
        if (
            // date for event has to be set in terms and date of event can be in the past
            (terms.maturityDate != 0 && (lastScheduleTime <= terms.maturityDate))
            // date for event has to come before previous candidate for the next event
            // && (scheduleTimeNextEvent == 0 || terms.maturityDate < scheduleTimeNextEvent)
            // avoid endless loop by requiring that the event is not the lastNonCyclicEvent
            && (lastScheduleTime != terms.maturityDate || lastEventType != EventType.MD)
        ) {
            eventTypeNextEvent = EventType.MD;
            scheduleTimeNextEvent = terms.maturityDate;
        }

        return encodeEvent(eventTypeNextEvent, scheduleTimeNextEvent);
    }

    /**
     * @notice Computes a schedule segment of cyclic contract events based on the contract terms
     * and the specified timestamps.
     * param terms terms of the contract
     * param lastScheduleTime last occurrence of cyclic event
     * param eventType eventType of the cyclic schedule
     * @return event schedule segment
     */
    function computeNextCyclicEvent(
        CECTerms calldata /* terms */,
        uint256 /* lastScheduleTime */,
        EventType /* eventType */
    )
        external
        pure
        override
        returns(bytes32)
    {
        return bytes32(0);
    }

    /**
     * @notice Verifies that the provided event is still scheduled under the terms, the current state of the
     * contract and the current state of the underlying.
     * param _event event for which to check if its still scheduled
     * param terms terms of the contract
     * param state current state of the contract
     * param hasUnderlying boolean indicating whether the contract has an underlying contract
     * param underlyingState state of the underlying (empty state object if non-existing)
     * @return boolean indicating whether event is still scheduled
     */
    function isEventScheduled(
        bytes32 /* _event */,
        CECTerms calldata /* terms */,
        State calldata /* state */,
        bool /* hasUnderlying */,
        State calldata /* underlyingState */
    )
        external
        pure
        override
        returns (bool)
    {
        // no contract performance for CEC
        return true;
    }

    /**
     * @notice Implements abstract method which is defined in BaseEngine.
     * Applies an event to the current state of the contract and returns the resulting state.
     * The inheriting Engine contract has to map the events type to the designated STF.
     * @param terms terms of the contract
     * @param state current state of the contract
     * @param _event event for which to evaluate the next state for
     * @param externalData external data needed for STF evaluation (e.g. rate for RR events)
     * @return the resulting contract state
     */
    function stateTransitionFunction(
        CECTerms memory terms,
        State memory state,
        bytes32 _event,
        bytes32 externalData
    )
        internal
        pure
        returns (State memory)
    {
        (EventType eventType, uint256 scheduleTime) = decodeEvent(_event);

        if (eventType == EventType.EXE) return STF_CEC_EXE(terms, state, scheduleTime, externalData);
        if (eventType == EventType.MD) return STF_CEC_MD(terms, state, scheduleTime, externalData);
        if (eventType == EventType.ST) return STF_CEC_ST(terms, state, scheduleTime, externalData);
        if (eventType == EventType.CE) return STF_CEC_CE(terms, state, scheduleTime, externalData);

        revert("CECEngine.stateTransitionFunction: ATTRIBUTE_NOT_FOUND");
    }

    /**
     * @notice Implements abstract method which is defined in BaseEngine.
     * Computes the payoff for an event under the current state of the contract.
     * The inheriting Engine contract has to map the events type to the designated POF.
     * @param terms terms of the contract
     * @param state current state of the contract
     * @param _event event for which the payoff should be evaluated
     * @param externalData external data needed for POF evaluation (e.g. fxRate)
     * @return the payoff of the event
     */
    function payoffFunction(
        CECTerms memory terms,
        State memory state,
        bytes32 _event,
        bytes32 externalData
    )
        internal
        pure
        returns (int256)
    {
        (EventType eventType, uint256 scheduleTime) = decodeEvent(_event);

        if (eventType == EventType.CE) return 0;
        if (eventType == EventType.EXE) return 0;
        if (eventType == EventType.ST) return POF_CEC_ST(terms, state, scheduleTime, externalData);
        if (eventType == EventType.MD) return 0;

        revert("CECEngine.payoffFunction: ATTRIBUTE_NOT_FOUND");
    }
}

contracts/ACTUS/Engines/CEC/CECSTF.sol.content:
// "SPDX-License-Identifier: Apache-2.0"
pragma solidity ^0.7.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/math/SignedSafeMath.sol";

import "../../Core/Core.sol";
import "../../Core/SignedMath.sol";


/**
 * @title STF
 * @notice Contains all state transition functions (STFs) currently used by all Engines
 */
contract CECSTF is Core {

    using SignedSafeMath for int;
    using SignedMath for int;


    /**
     * State transition for PAM credit events
     * @param state the old state
     * @return the new state
     */
    function STF_CEC_CE (
        CECTerms memory /* terms */,
        State memory state,
        uint256 /* scheduleTime */,
        bytes32 /* externalData */
    )
        internal
        pure
        returns(State memory)
    {
        return state;
    }

    function STF_CEC_MD (
        CECTerms memory /* terms */,
        State memory state,
        uint256 scheduleTime,
        bytes32 /* externalData */
    )
        internal
        pure
        returns (State memory)
    {
        state.notionalPrincipal = 0;
        state.contractPerformance = ContractPerformance.MD;
        state.statusDate = scheduleTime;

        return state;
    }

    function STF_CEC_EXE (
        CECTerms memory terms,
        State memory state,
        uint256 scheduleTime,
        bytes32 externalData
    )
        internal
        pure
        returns (State memory)
    {
        int256 timeFromLastEvent;
        {
            timeFromLastEvent = yearFraction(
                shiftCalcTime(state.statusDate, terms.businessDayConvention, terms.calendar, terms.maturityDate),
                shiftCalcTime(scheduleTime, terms.businessDayConvention, terms.calendar, terms.maturityDate),
                terms.dayCountConvention,
                terms.maturityDate
            );
        }
        state.statusDate = scheduleTime;
        // decode state.notionalPrincipal of underlying from externalData
        state.exerciseAmount = terms.coverageOfCreditEnhancement.floatMult(int256(externalData));
        state.exerciseDate = scheduleTime;

        if (terms.feeBasis == FeeBasis.A) {
            state.feeAccrued = roleSign(terms.contractRole) * terms.feeRate;
        } else {
            state.feeAccrued = state.feeAccrued
            .add(
                timeFromLastEvent
                .floatMult(terms.feeRate)
                .floatMult(state.notionalPrincipal)
            );
        }

        return state;
    }

    function STF_CEC_ST (
        CECTerms memory /* terms */,
        State memory state,
        uint256 scheduleTime,
        bytes32 /* externalData */
    )
        internal
        pure
        returns (State memory)
    {
        state.notionalPrincipal = 0;
        state.feeAccrued = 0;
        state.contractPerformance = ContractPerformance.MD;
        state.statusDate = scheduleTime;

        return state;
    }
}

